import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories'; // Keep this if you have categories here
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phoneNumber: '',
    gender: '',
    address: ''
  });

  const handleMyProfileClick = async () => {
    try {
      setShowProfile(!showProfile); // Toggle profile view
      const token = localStorage.getItem('token');
      const response = await axios.get('https://localhost:7222/Admin/Profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(response.data);
      setFormData(response.data);
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put('https://localhost:7222/Admin/Update', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(formData);
      setEditMode(false);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/';
  };

  return (
    <div className="container mt-4">
      {/* Navigation Buttons */}
      <div className="d-flex justify-content-end gap-2 mb-4">
        <button className="btn btn-primary" onClick={handleMyProfileClick}>
          My Profile
        </button>
        <button className="btn btn-danger" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Profile Section */}
      {showProfile && user && (
        <div className="card p-4 mb-4">
          <h4 className="mb-3">User Profile</h4>
          {editMode ? (
            <>
              <div className="mb-2">
                <label>Name:</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className="form-control"
                />
              </div>
              <div className="mb-2">
                <label>Email:</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="form-control"
                />
              </div>
              <div className="mb-2">
                <label>Phone Number:</label>
                <input
                  type="text"
                  name="phoneNumber"
                  value={formData.phoneNumber}
                  onChange={handleChange}
                  className="form-control"
                />
              </div>
              <div className="mb-2">
                <label>Gender:</label>
                <input
                  type="text"
                  name="gender"
                  value={formData.gender}
                  onChange={handleChange}
                  className="form-control"
                />
              </div>
              <div className="mb-2">
                <label>Address:</label>
                <input
                  type="text"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  className="form-control"
                />
              </div>
              <button className="btn btn-success mt-2" onClick={handleSaveClick}>
                Save
              </button>
            </>
          ) : (
            <>
              <p><strong>Name:</strong> {user.name}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Phone Number:</strong> {user.phoneNumber}</p>
              <p><strong>Gender:</strong> {user.gender}</p>
              <p><strong>Address:</strong> {user.address}</p>
              <button className="btn btn-warning" onClick={handleEditClick}>
                Edit
              </button>
            </>
          )}
        </div>
      )}

      {/* Categories Section */}
      <Categories />
    </div>
  );
}

export default Welcome;
