import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductService from '../services/ProductService';
import axios from 'axios';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    description: '',
    price: '',
    category: '',
    imageURL: ''
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    ProductService.getProducts()
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error('Error fetching products', error);
      });
  };

  const fetchProductsByCategory = (category) => {
    ProductService.getProductsByCategory(category)
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error('Error fetching category products', error);
      });
  };

  const handleDelete = (id) => {
    ProductService.deleteProduct(id)
      .then(() => {
        fetchProducts();
      })
      .catch((error) => {
        console.error('Error deleting product', error);
      });
  };

  // --- Add Product handlers ---

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    try {
      await axios.post('http://localhost:5144/Product', newProduct, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      alert('Product added successfully!');
      setShowAddForm(false);
      setNewProduct({
        productName: '',
        description: '',
        price: '',
        category: '',
        imageURL: ''
      });
      fetchProducts(); // Refresh product list
    } catch (error) {
      console.error('Error adding product:', error);
      alert('Failed to add product.');
    }
  };

  return (
    <div>
      <h2>Product List</h2>

      {/* Toggle Add Product Form */}
      <button
        onClick={() => setShowAddForm(prev => !prev)}
        className="btn btn-primary mb-3"
      >
        {showAddForm ? 'Cancel Add Product' : 'Add Product'}
      </button>

      {/* Add Product Form */}
      {showAddForm && (
        <form onSubmit={handleAddProduct} className="mb-4">
          <div>
            <label>Product Name:</label>
            <input
              type="text"
              name="productName"
              value={newProduct.productName}
              onChange={handleInputChange}
              required
              className="form-control"
            />
          </div>

          <div>
            <label>Description:</label>
            <input
              type="text"
              name="description"
              value={newProduct.description}
              onChange={handleInputChange}
              required
              className="form-control"
            />
          </div>

          <div>
            <label>Price:</label>
            <input
              type="number"
              name="price"
              value={newProduct.price}
              onChange={handleInputChange}
              required
              className="form-control"
            />
          </div>

          <div>
            <label>Category:</label>
            <input
              type="text"
              name="category"
              value={newProduct.category}
              onChange={handleInputChange}
              required
              className="form-control"
            />
          </div>

          <div>
            <label>Image URL:</label>
            <input
              type="text"
              name="imageURL"
              value={newProduct.imageURL}
              onChange={handleInputChange}
              className="form-control"
            />
          </div>

          <button type="submit" className="btn btn-success mt-2">
            Add Product
          </button>
        </form>
      )}

      <div style={{ marginBottom: '20px' }}>
        <button onClick={() => fetchProductsByCategory('Woman')}>Woman</button>
        <button onClick={() => fetchProductsByCategory('Men')} style={{ marginLeft: '10px' }}>
          Men
        </button>
        <button onClick={fetchProducts} style={{ marginLeft: '10px' }}>
          All
        </button>
      </div>

      <ul style={{ marginTop: '20px' }}>
        {products.map((product) => (
          <li key={product.productId} style={{ marginBottom: '10px' }}>
            <strong>{product.productName}</strong> - ${product.price}
            <img src={product.imageURL} alt={product.productName} height={50} width={100} style={{ marginLeft: '10px' }} />
            <Link
              to={`/edit/${product.productId}`}
              style={{ marginLeft: '10px', backgroundColor: 'blue', color: 'white', padding: '4px 8px', borderRadius: '4px', textDecoration: 'none' }}
            >
              Edit
            </Link>
            <button
              onClick={() => handleDelete(product.productId)}
              style={{ marginLeft: '10px', backgroundColor: 'red', color: 'white', border: 'none', padding: '4px 8px', borderRadius: '4px' }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;
