import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductService from '../../services/ProductService';
import myImage from '../../images/Woman.jpg';

function Categories() {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get("http://localhost:5144/Category", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCategories(response.data);
      } catch (error) {
        console.error("Failed to fetch categories:", error);
      }
    };

    fetchCategories();
  }, []);

  const handleCategoryClick = async (categoryName) => {
    setSelectedCategory(categoryName);
    try {
      const response = await ProductService.getProductsByCategory(categoryName);
      setProducts(response.data);
    } catch (error) {
      console.error("Failed to fetch products:", error);
      setProducts([]);
    }
  };

  const handleAddToCart = (product) => {
    // You can update this to call an API or use context/global state
    console.log("Adding to cart:", product);
    alert(`Added ${product.productName} to cart!`);
  };

  return (
    <div className="container mt-4">
      <h4>Categories</h4>
      <div className="row mb-4">
        {categories.map((category) => (
          <div className="col-md-3 mb-2" key={category.categoryId}>
            <button
              className="btn btn-outline-primary w-100"
              onClick={() => handleCategoryClick(category.name)}
            >
              {category.name}
            </button>
          </div>
        ))}
      </div>

      {selectedCategory && (
        <>
          <h5>Products in "{selectedCategory}"</h5>
          <div className="row">
            {products.length > 0 ? (
              products.map(product => (
                <div key={product.productId} className="col-md-3 mb-3">
                  <div className="card h-100">
                    <img
                      src={myImage}
                      alt="Product"
                      className="card-img-top"
                      style={{ height: '200px', objectFit: 'cover' }}
                    />
                    <div className="card-body d-flex flex-column">
                      <h6 className="card-title">{product.productName}</h6>
                      <p className="card-text">{product.description}</p>
                      <p className="card-text text-success">Rs {product.price}</p>
                      <button
                        className="btn btn-sm btn-primary mt-auto"
                        onClick={() => handleAddToCart(product)}
                      >
                        Add to Cart
                      </button>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <p>No products found in this category.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default Categories;
