import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    role: '',
    shippingAddress: '',
    paymentDetails: ''
  });

  const fetchUserProfile = async () => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role'); // Make sure this is set during login

    if (!token || !role) return;

    const endpoint =
      role === 'Admin'
        ? 'https://localhost:5144/Admin/profile'
        : 'https://localhost:5144/User/profile';

    try {
      const response = await axios.get(endpoint, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const data = response.data;

      setFormData({
        name: data.name || '',
        email: data.email || '',
        phone: data.phone || '',
        role: data.role || '',
        shippingAddress: data.shippingAddress || '',
        paymentDetails: data.paymentDetails || ''
      });
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  useEffect(() => {
    if (showProfile) {
      fetchUserProfile(); // fetch only when showing profile
    }
  }, [showProfile]);

  const handleEditClick = () => setEditMode(true);

  const handleSaveClick = () => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');

    const endpoint =
      role === 'Admin'
        ? 'https://localhost:5144/Admin/Profile'
        : 'https://localhost:5144/User/rofile';

    axios
      .put(endpoint, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setFormData(response.data);
        setEditMode(false);
        alert('Profile updated successfully!');
      })
      .catch((error) => {
        console.error('Error updating profile:', error);
        alert('Failed to update profile.');
      });
  };

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    window.location.href = '/login';
  };

  const handleHome = () => {
    setShowProfile(false);
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-end mb-3">
        <button className="btn btn-info me-2" onClick={() => setShowProfile(true)}>
          My Profile
        </button>
        <button className="btn btn-secondary me-2" onClick={handleHome}>
          Home
        </button>
        <button className="btn btn-danger" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {showProfile ? (
        <div className="card p-4 shadow">
          <h3 className="mb-4">User Profile</h3>
          <form>
            <div className="mb-3">
              <label>Name:</label>
              <input
                type="text"
                className="form-control"
                name="name"
                value={formData.name}
                onChange={handleChange}
                disabled={!editMode}
              />
            </div>
            <div className="mb-3">
              <label>Email:</label>
              <input
                type="email"
                className="form-control"
                name="email"
                value={formData.email}
                onChange={handleChange}
                disabled={!editMode}
              />
            </div>
            <div className="mb-3">
              <label>Phone:</label>
              <input
                type="text"
                className="form-control"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                disabled={!editMode}
              />
            </div>
            {formData.shippingAddress !== undefined && (
              <div className="mb-3">
                <label>Shipping Address:</label>
                <input
                  type="text"
                  className="form-control"
                  name="shippingAddress"
                  value={formData.shippingAddress}
                  onChange={handleChange}
                  disabled={!editMode}
                />
              </div>
            )}
            {formData.paymentDetails !== undefined && (
              <div className="mb-3">
                <label>Payment Details:</label>
                <input
                  type="text"
                  className="form-control"
                  name="paymentDetails"
                  value={formData.paymentDetails}
                  onChange={handleChange}
                  disabled={!editMode}
                />
              </div>
            )}
            <div className="mb-3">
              <label>Role:</label>
              <input
                type="text"
                className="form-control"
                name="role"
                value={formData.role}
                disabled
              />
            </div>

            {editMode ? (
              <button
                type="button"
                className="btn btn-success"
                onClick={handleSaveClick}
              >
                Save
              </button>
            ) : (
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleEditClick}
              >
                Edit
              </button>
            )}
          </form>
        </div>
      ) : (
        <Categories />
      )}
    </div>
  );
}

export default Welcome;
