import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
  });

  // Load user data from /User/dataend on mount
  useEffect(() => {
    axios.get('http://localhost:5000/User/dataend')
      .then((response) => {
        setUser(response.data);
        setFormData({
          name: response.data.name || '',
          email: response.data.email || '',
          phone: response.data.phone || '',
          address: response.data.address || '',
        });
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  const handleEdit = () => {
    setEditMode(true);
  };

  const handleCancel = () => {
    setEditMode(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSave = () => {
    axios.put('http://localhost:5000/User/update', formData)
      .then((response) => {
        setUser(response.data);
        setEditMode(false);
      })
      .catch((error) => {
        console.error('Error saving profile:', error);
      });
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between mb-3">
        <h2>Welcome {user?.name}</h2>
        <div>
          <button className="btn btn-primary me-2" onClick={() => setShowProfile(false)}>Home</button>
          <button className="btn btn-secondary" onClick={() => setShowProfile(true)}>My Profile</button>
        </div>
      </div>

      {!showProfile ? (
        <Categories />
      ) : (
        <div className="card p-4">
          <h4 className="mb-3">My Profile</h4>
          <div className="mb-3">
            <label className="form-label">Name:</label>
            {editMode ? (
              <input
                type="text"
                className="form-control"
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
            ) : (
              <p>{user?.name}</p>
            )}
          </div>

          <div className="mb-3">
            <label className="form-label">Email:</label>
            {editMode ? (
              <input
                type="email"
                className="form-control"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
            ) : (
              <p>{user?.email}</p>
            )}
          </div>

          <div className="mb-3">
            <label className="form-label">Phone:</label>
            {editMode ? (
              <input
                type="text"
                className="form-control"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
              />
            ) : (
              <p>{user?.phone}</p>
            )}
          </div>

          <div className="mb-3">
            <label className="form-label">Address:</label>
            {editMode ? (
              <input
                type="text"
                className="form-control"
                name="address"
                value={formData.address}
                onChange={handleChange}
              />
            ) : (
              <p>{user?.address}</p>
            )}
          </div>

          <div>
            {editMode ? (
              <>
                <button className="btn btn-success me-2" onClick={handleSave}>Save</button>
                <button className="btn btn-danger" onClick={handleCancel}>Cancel</button>
              </>
            ) : (
              <button className="btn btn-warning" onClick={handleEdit}>Edit</button>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Welcome;
