import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [user, setUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState(null);
  const [showCart, setShowCart] = useState(false);
  const [cartItems, setCartItems] = useState([]);

  const navigate = useNavigate();

  // Modified logout to clear only auth, not cart
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('email');
    // Do NOT clear cartItems from localStorage
    navigate('/login');
  };

  // Load user profile and cart on mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    const email = localStorage.getItem('email');

    if (!token || !email) {
      navigate('/login');
      return;
    }

    try {
      const tokenData = JSON.parse(atob(token.split('.')[1]));
      const role = tokenData.role || 'User';

      const endpoint =
        role === 'Admin'
          ? `http://localhost:5144/Admin/Profile?email=${email}`
          : `http://localhost:5144/User/Profile?email=${email}`;

      axios
        .get(endpoint, { headers: { Authorization: `Bearer ${token}` } })
        .then((response) => {
          setUser(response.data);
          setUpdatedUser(response.data);

          // Load cart from localStorage (persistent)
          const savedCart = localStorage.getItem(`cartItems_${response.data.userId}`);
          if (savedCart) {
            setCartItems(JSON.parse(savedCart));
          } else {
            setCartItems([]);
          }
        })
        .catch(() => {
          navigate('/login');
        });
    } catch {
      logout();
    }
  }, [navigate]);

  // Sync cart to localStorage on changes
  useEffect(() => {
    if (user && user.userId) {
      localStorage.setItem(`cartItems_${user.userId}`, JSON.stringify(cartItems));
    }
  }, [cartItems, user]);

  const handleAddToCart = (product) => {
    setCartItems((prevCart) => [...prevCart, product]);
  };

  const handleRemoveFromCart = (indexToRemove) => {
    setCartItems((prevCart) => prevCart.filter((_, i) => i !== indexToRemove));
  };

  const handleLogout = () => {
    setCartItems([]); // clear UI cart state
    setUser(null);
    setShowProfile(false);
    setShowCart(false);
    logout();
  };

  // You can keep the rest of your profile update, delete handlers and UI unchanged...

  return (
    <div className="container-fluid mt-3">
      {/* Header Bar */}
      <div className="border border-2 p-4 mb-4">
        <div className="d-flex justify-content-between align-items-center">
          <h2>Welcome to the Dashboard</h2>
          <div>
            <button
              className="btn btn-outline-primary me-2"
              onClick={() => {
                setShowProfile(true);
                setShowCart(false);
              }}
            >
              My Profile
            </button>
            <button
              className="btn btn-outline-success me-2"
              onClick={() => {
                setShowCart(true);
                setShowProfile(false);
              }}
            >
              Cart ({cartItems.length})
            </button>
            <button className="btn btn-outline-danger" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </div>

      {/* Profile Section */}
      {showProfile && user && (
        <div className="card p-3 shadow mb-4 position-relative">
          <button
            className="btn-close position-absolute top-0 end-0 m-3"
            onClick={() => setShowProfile(false)}
            aria-label="Close"
          ></button>
          <h3>My Profile</h3>
          {!editMode ? (
            <>
              <p>
                <strong>Name:</strong> {user.name}
              </p>
              <p>
                <strong>Email:</strong> {user.email}
              </p>
              <p>
                <strong>Role:</strong> {user.role}
              </p>
              {user.shippingAddress && (
                <p>
                  <strong>Shipping Address:</strong> {user.shippingAddress}
                </p>
              )}
              {user.paymentDetails && (
                <p>
                  <strong>Payment Details:</strong> {user.paymentDetails}
                </p>
              )}
              {user.permissions && (
                <p>
                  <strong>Permissions:</strong> {user.permissions}
                </p>
              )}
              <div className="mt-3">
                <button className="btn btn-warning me-2" onClick={() => setEditMode(true)}>
                  Update Profile
                </button>
                <button
                  className="btn btn-outline-danger"
                  onClick={async () => {
                    const confirmDelete = window.confirm('Are you sure you want to delete your account?');
                    if (!confirmDelete) return;

                    try {
                      const token = localStorage.getItem('token');
                      await axios.delete(`http://localhost:5144/User/Delete?id=${user.userId}`, {
                        headers: { Authorization: `Bearer ${token}` },
                      });
                      alert('Account deleted successfully.');
                      localStorage.removeItem(`cartItems_${user.userId}`);
                      handleLogout();
                    } catch (error) {
                      alert('Failed to delete account.');
                    }
                  }}
                >
                  Delete Account
                </button>
              </div>
            </>
          ) : (
            <>
              <div className="mb-2">
                <label>
                  <strong>Name:</strong>
                </label>
                <input
                  className="form-control"
                  type="text"
                  value={updatedUser.name}
                  onChange={(e) => setUpdatedUser({ ...updatedUser, name: e.target.value })}
                />
              </div>
              <div className="mb-2">
                <label>
                  <strong>Email:</strong>
                </label>
                <input
                  className="form-control"
                  type="email"
                  value={updatedUser.email}
                  onChange={(e) => setUpdatedUser({ ...updatedUser, email: e.target.value })}
                />
              </div>
              {updatedUser.shippingAddress !== undefined && (
                <div className="mb-2">
                  <label>
                    <strong>Shipping Address:</strong>
                  </label>
                  <input
                    className="form-control"
                    type="text"
                    value={updatedUser.shippingAddress}
                    onChange={(e) => setUpdatedUser({ ...updatedUser, shippingAddress: e.target.value })}
                  />
                </div>
              )}
              {updatedUser.paymentDetails !== undefined && (
                <div className="mb-2">
                  <label>
                    <strong>Payment Details:</strong>
                  </label>
                  <input
                    className="form-control"
                    type="text"
                    value={updatedUser.paymentDetails}
                    onChange={(e) => setUpdatedUser({ ...updatedUser, paymentDetails: e.target.value })}
                  />
                </div>
              )}
              <div className="mb-2">
                <label>
                  <strong>Role:</strong>
                </label>
                <input className="form-control" type="text" value={updatedUser.role} readOnly />
              </div>
              {updatedUser.permissions && (
                <div className="mb-2">
                  <label>
                    <strong>Permissions:</strong>
                  </label>
                  <input className="form-control" type="text" value={updatedUser.permissions} readOnly />
                </div>
              )}
              <div className="mt-3">
                <button
                  className="btn btn-success me-2"
                  onClick={async () => {
                    try {
                      const token = localStorage.getItem('token');
                      await axios.put(`http://localhost:5144/User/Update?id=${updatedUser.userId}`, updatedUser, {
                        headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'application/json' },
                      });
                      alert('Profile updated successfully!');
                      setUser(updatedUser);
                      setEditMode(false);
                    } catch (error) {
                      alert('Failed to update profile.');
                    }
                  }}
                >
                  Save Changes
                </button>
                <button
                  className="btn btn-secondary"
                  onClick={() => {
                    setEditMode(false);
                    setUpdatedUser(user);
                  }}
                >
                  Cancel
                </button>
              </div>
            </>
          )}
        </div>
      )}

      {/* Cart Section */}
      {showCart && (
        <div className="card p-3 shadow mb-4">
          <h3>Your Cart</h3>
          {cartItems.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <ul className="list-group">
              {cartItems.map((item, index) => (
                <li key={index} className="list-group-item">
                  <div className="d-flex justify-content-between align-items-start flex-column">
                    <div className="fw-bold">{item.productName}</div>
                    <div className="text-muted">{item.description}</div>
                    <div className="text-success mt-2">Rs {item.price}</div>
                    <button className="btn btn-sm btn-danger mt-2" onClick={() => handleRemoveFromCart(index)}>
                      Remove
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}

      {/* Categories with add to cart handler */}
      <Categories onAddToCart={handleAddToCart} />
    </div>
  );
}

export default Welcome;
