import React, { useState, useEffect } from "react";
import axios from "axios";

const API_BASE = "http://localhost:5144";

const getToken = () => localStorage.getItem("token");

const getConfig = () => ({
  headers: { Authorization: `Bearer ${getToken()}` },
});

function AdminPanel() {
  // Login form state
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState("");

  // Admin info + list + editing
  const [currentAdmin, setCurrentAdmin] = useState(null);
  const [admins, setAdmins] = useState([]);
  const [message, setMessage] = useState("");
  const [editAdminId, setEditAdminId] = useState(null);
  const [loading, setLoading] = useState(false);

  // Form for adding/editing admin
  const [formAdmin, setFormAdmin] = useState({
    AdminId: 0,
    Name: "",
    Role: "",
    Permissions: "",
    Email: "",
    Password: "",
  });

  // On component mount, load profile if logged in
  useEffect(() => {
    const token = getToken();
    if (token) {
      fetchProfile();
      fetchAdmins();
    }
  }, []);

  // Login handler
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError("");
    setMessage("");
    try {
      const loginUrl = `${API_BASE}/Admin/Login`;
      const res = await axios.post(loginUrl, { email, password });
      const token = res.data.token;
      if (!token) {
        setLoginError("Login failed: No token received.");
        return;
      }
      localStorage.setItem("token", token);
      localStorage.setItem("email", email);
      setEmail("");
      setPassword("");
      fetchProfile();
      fetchAdmins();
    } catch (err) {
      console.error("Login error:", err);
      setLoginError(
        err.response?.data?.message || "Invalid email or password"
      );
    }
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    setCurrentAdmin(null);
    setAdmins([]);
    setEditAdminId(null);
    setMessage("");
    setLoginError("");
  };

  // Fetch logged-in admin profile
  const fetchProfile = async () => {
    setLoading(true);
    setLoginError("");
    setMessage("");
    try {
      const emailStored = localStorage.getItem("email");
      if (!emailStored) {
        handleLogout();
        return;
      }
      const profileUrl = `${API_BASE}/Admin/Profile?email=${encodeURIComponent(
        emailStored
      )}`;
      const res = await axios.get(profileUrl, getConfig());
      setCurrentAdmin(res.data);
    } catch (error) {
      console.error("Fetch profile error:", error);
      handleLogout();
    } finally {
      setLoading(false);
    }
  };

  // Fetch all admins
  const fetchAdmins = async () => {
    try {
      const listUrl = `${API_BASE}/Admin/ListofAdmins`;
      const res = await axios.get(listUrl, getConfig());
      setAdmins(res.data);
    } catch (err) {
      console.error("Failed to fetch admins", err);
      setMessage(
        err.response?.data?.message || "Failed to load admins list."
      );
    }
  };

  // Fill form for editing
  const startEdit = (admin) => {
    setFormAdmin({
      AdminId: admin.AdminId,
      Name: admin.Name,
      Role: admin.Role,
      Permissions: admin.Permissions,
      Email: admin.Email,
      Password: "", // leave empty, optional change
    });
    setEditAdminId(admin.AdminId);
    setMessage("");
  };

  // Reset form to empty (add mode)
  const resetForm = () => {
    setFormAdmin({
      AdminId: 0,
      Name: "",
      Role: "",
      Permissions: "",
      Email: "",
      Password: "",
    });
    setEditAdminId(null);
    setMessage("");
  };

  // Handle form input changes
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormAdmin((prev) => ({ ...prev, [name]: value }));
  };

  // Submit form - add or update
  const handleSubmitForm = async (e) => {
    e.preventDefault();

    if (!formAdmin.Name || !formAdmin.Role || !formAdmin.Email) {
      setMessage("Please fill all required fields: Name, Role, Email.");
      return;
    }

    if (!editAdminId && !formAdmin.Password) {
      setMessage("Password is required for new admin.");
      return;
    }

    try {
      if (editAdminId) {
        // Update admin
        const updateUrl = `${API_BASE}/Admin/Update?id=${formAdmin.AdminId}`;
        const res = await axios.put(updateUrl, formAdmin, getConfig());
        setMessage(res.data.message || "Admin updated successfully.");
      } else {
        // Create new admin
        const createUrl = `${API_BASE}/Admin/CreateAdmin`;
        const res = await axios.post(createUrl, formAdmin, getConfig());
        setMessage(res.data.message || "Admin created successfully.");
      }
      resetForm();
      fetchAdmins();
    } catch (err) {
      console.error("Operation failed:", err);
      setMessage(
        err.response?.data?.message || "Operation failed. Check console for details."
      );
    }
  };

  // Delete admin
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this admin?")) return;

    try {
      const deleteUrl = `${API_BASE}/Admin/Delete?id=${id}`;
      const res = await axios.delete(deleteUrl, getConfig());
      setMessage(res.data.message || "Admin deleted.");
      fetchAdmins();
      if (editAdminId === id) resetForm();
    } catch (err) {
      console.error("Delete failed:", err);
      setMessage(
        err.response?.data?.message || "Delete failed."
      );
    }
  };

  // Loading spinner or message
  if (loading) return <p>Loading profile...</p>;

  // If no token / not logged in, show login form
  if (!currentAdmin) {
    return (
      <div style={{ maxWidth: 400, margin: "auto", padding: 20 }}>
        <h2>Admin Login</h2>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ width: "100%", padding: 8, marginBottom: 10 }}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: "100%", padding: 8, marginBottom: 10 }}
          />
          <button type="submit" style={{ padding: 10, width: "100%" }}>
            Login
          </button>
        </form>
        {loginError && <p style={{ color: "red" }}>{loginError}</p>}
      </div>
    );
  }

  // Logged in: show admin dashboard and management
  return (
    <div style={{ maxWidth: 900, margin: "auto", padding: 20 }}>
      <h2>
        Welcome, {currentAdmin.Name} (Role: {currentAdmin.Role})
      </h2>
      <button onClick={handleLogout} style={{ marginBottom: 20 }}>
        Logout
      </button>

      <h3>Manage Admins</h3>

      {/* Add / Edit admin form */}
      <form onSubmit={handleSubmitForm} style={{ marginBottom: 20 }}>
        <input
          type="text"
          name="Name"
          placeholder="Name *"
          value={formAdmin.Name}
          onChange={handleFormChange}
          required
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <input
          type="email"
          name="Email"
          placeholder="Email *"
          value={formAdmin.Email}
          onChange={handleFormChange}
          required
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <input
          type="text"
          name="Role"
          placeholder="Role *"
          value={formAdmin.Role}
          onChange={handleFormChange}
          required
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <input
          type="text"
          name="Permissions"
          placeholder="Permissions"
          value={formAdmin.Permissions}
          onChange={handleFormChange}
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <input
          type="password"
          name="Password"
          placeholder={editAdminId ? "New Password (optional)" : "Password *"}
          value={formAdmin.Password}
          onChange={handleFormChange}
          required={!editAdminId}
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />

        <button type="submit" style={{ padding: 10, marginRight: 10 }}>
          {editAdminId ? "Update Admin" : "Add Admin"}
        </button>
        {editAdminId && (
          <button type="button" onClick={resetForm} style={{ padding: 10 }}>
            Cancel
          </button>
        )}
      </form>

      {/* Status message */}
      {message && <p>{message}</p>}

      {/* Admins table */}
      <table
        border="1"
        cellPadding="8"
        style={{ width: "100%", borderCollapse: "collapse" }}
      >
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Permissions</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {admins.length === 0 && (
            <tr>
              <td colSpan="5" style={{ textAlign: "center" }}>
                No admins found.
              </td>
            </tr>
          )}
          {admins.map((admin) => (
            <tr key={admin.AdminId}>
              <td>{admin.Name}</td>
              <td>{admin.Email}</td>
              <td>{admin.Role}</td>
              <td>{admin.Permissions}</td>
              <td>
                <button
                  onClick={() => startEdit(admin)}
                  style={{ marginRight: 10 }}
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(admin.AdminId)}
                  style={{ color: "red" }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPanel;
