import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Welcome.css";
import ProductCard from "./ProductCard";
import Profile from "./Profile";
import Cart from "../Cart";

function Welcome({ email, logout }) {
  const [user, setUser] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [showProfile, setShowProfile] = useState(false);
  const [showCart, setShowCart] = useState(false);

  const CART_KEY = "userCart"; // constant key for cart persistence

  // Load cart from localStorage when component mounts
  useEffect(() => {
    const savedCart = localStorage.getItem(CART_KEY);
    if (savedCart) {
      setCartItems(JSON.parse(savedCart));
    }
  }, []);

  // Fetch user details from backend
  useEffect(() => {
    if (!email) return;

    axios
      .get(`https://localhost:7110/User/${email}`)
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => console.error("Error fetching user data:", error));
  }, [email]);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem(CART_KEY, JSON.stringify(cartItems));
  }, [cartItems]);

  // Add a product to the cart
  const addToCart = (product) => {
    setCartItems((prevItems) => {
      const existingItem = prevItems.find(
        (item) => item.productId === product.productId
      );
      if (existingItem) {
        return prevItems.map((item) =>
          item.productId === product.productId
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prevItems, { ...product, quantity: 1 }];
    });
  };

  // Remove a product from the cart
  const removeFromCart = (productId) => {
    setCartItems((prevItems) =>
      prevItems
        .map((item) =>
          item.productId === productId
            ? { ...item, quantity: item.quantity - 1 }
            : item
        )
        .filter((item) => item.quantity > 0)
    );
  };

  // Logout user but keep the cart in localStorage
  const handleLogout = () => {
    setUser(null);
    setShowProfile(false);
    setShowCart(false);
    logout();
  };

  return (
    <div className="welcome-container">
      {user && (
        <div className="header">
          <h2>
            Welcome, {user.firstName} {user.lastName}!
          </h2>
          <button onClick={handleLogout}>Logout</button>
          <button onClick={() => setShowProfile(!showProfile)}>Profile</button>
          <button onClick={() => setShowCart(!showCart)}>
            Cart ({cartItems.reduce((total, item) => total + item.quantity, 0)})
          </button>
        </div>
      )}

      {showProfile && <Profile user={user} />}
      {showCart && (
        <Cart cartItems={cartItems} removeFromCart={removeFromCart} />
      )}

      <div className="products">
        {/* Example static products â€” replace with your product API */}
        {[
          { productId: 1, name: "Product A", price: 10 },
          { productId: 2, name: "Product B", price: 20 },
        ].map((product) => (
          <ProductCard
            key={product.productId}
            product={product}
            addToCart={addToCart}
          />
        ))}
      </div>
    </div>
  );
}

export default Welcome;
