import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({ name: '' });

  // Load user data when profile is shown
  useEffect(() => {
    if (showProfile) {
      axios.get('https://localhost:7099/User/Profile', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      })
      .then(res => {
        setUser(res.data);
        setFormData({ name: res.data.name }); // Use only "name"
      })
      .catch(err => console.error(err));
    }
  }, [showProfile]);

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = () => {
    axios.put('https://localhost:7099/User/UpdateProfile', formData, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    })
    .then(res => {
      setUser({ ...user, name: formData.name });
      setEditMode(false);
    })
    .catch(err => console.error(err));
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  const isLoggedIn = !!localStorage.getItem('token');

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Welcome</h2>
        <div>
          {isLoggedIn ? (
            <>
              <button className="btn btn-primary me-2" onClick={() => setShowProfile(!showProfile)}>
                {showProfile ? 'Hide Profile' : 'My Profile'}
              </button>
              <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
            </>
          ) : (
            <a className="btn btn-success" href="/login">Login</a>
          )}
        </div>
      </div>

      {showProfile && user && (
        <div className="card mb-4">
          <div className="card-header">My Profile</div>
          <div className="card-body">
            {editMode ? (
              <>
                <div className="mb-3">
                  <label className="form-label">Name</label>
                  <input
                    type="text"
                    className="form-control"
                    value={formData.name}
                    onChange={(e) => setFormData({ name: e.target.value })}
                  />
                </div>
                <button className="btn btn-success" onClick={handleSaveClick}>Save</button>
              </>
            ) : (
              <>
                <p><strong>Name:</strong> {user.name}</p>
                <button className="btn btn-secondary" onClick={handleEditClick}>Edit</button>
              </>
            )}
          </div>
        </div>
      )}

      {/* Show category cards */}
      <Categories />
    </div>
  );
}

export default Welcome;
