import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);

  const token = localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  const handleHomeClick = () => {
    setShowProfile(false);
  };

  const handleProfileClick = async () => {
    setShowProfile(true);
    await fetchUserProfile();
  };

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get('https://localhost:7184/User/Profile', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setUser(response.data);
    } catch (error) {
      console.error('Unable to load profile', error);
    }
  };

  // Fetch profile if already logged in and showProfile was not yet clicked
  useEffect(() => {
    if (!user && token) {
      fetchUserProfile();
    }
  }, []);

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Welcome{user ? `, ${user.name}` : ''}</h2>
        <div>
          <button className="btn btn-primary me-2" onClick={handleHomeClick}>Home</button>
          <button className="btn btn-info me-2" onClick={handleProfileClick}>My Profile</button>
          <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
        </div>
      </div>

      {showProfile ? (
        user ? (
          <div className="card p-4">
            <h3>My Profile</h3>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Role:</strong> {user.role}</p>
          </div>
        ) : (
          <div className="text-center my-4">Loading profile...</div>
        )
      ) : (
        <Categories />
      )}
    </div>
  );
}

export default Welcome;
