import axios from 'axios';
import AuthService from './AuthService';

const API_URL = 'http://localhost:5144/Category';

class CategoryService {
  getHeaders() {
    const token = AuthService.getToken(); // or localStorage.getItem("token");
    return {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };
  }

  // Get all categories
  getCategories() {
    return axios.get(API_URL, this.getHeaders());
  }

  // Add a new category
  addCategory(category) {
    return axios.post(`${API_URL}/AddingNewCategory`, category, this.getHeaders());
  }

  // Update category
  updateCategory(id, name) {
    return axios.put(`${API_URL}/UpdateCategoryid=${id}`, null, {
      params: { id, name },
      ...this.getHeaders(),
    });
  }

  // Delete category
  deleteCategory(id) {
    return axios.delete(`${API_URL}/DeleteCategory?id=${id}`, this.getHeaders());
  }
}

export default new CategoryService();
