// src/pages/Welcome.js
import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Welcome({ logout }) {
  const [showProfile, setShowProfile] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [user, setUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState(null);
  const [showCart, setShowCart] = useState(false);
  const [cartItems, setCartItems] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }
  }, [navigate]);

  const handleAddToCart = (product) => {
    setCartItems(prevCart => [...prevCart, product]);
  };

  const handleRemoveFromCart = (indexToRemove) => {
    setCartItems(prevCart => prevCart.filter((_, index) => index !== indexToRemove));
  };

  const handleProfileClick = async () => {
    const token = localStorage.getItem('token');
    const email = localStorage.getItem('email');

    if (!token || !email) {
      alert('You are not logged in');
      return;
    }

    try {
      const tokenData = JSON.parse(atob(token.split('.')[1]));
      const role = tokenData.role || 'User';

      const endpoint =
        role === 'Admin'
          ? `http://localhost:5144/Admin/Profile?email=${email}`
          : `http://localhost:5144/User/Profile?email=${email}`;

      const response = await axios.get(endpoint, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setUser(response.data);
      setUpdatedUser(response.data);
      setShowProfile(true);
      setEditMode(false);
      setShowCart(false);
    } catch (error) {
      console.error('Error fetching profile:', error);
      alert('Failed to load profile. Please try again.');
    }
  };

  const handleUpdate = async () => {
    const token = localStorage.getItem('token');
    try {
      await axios.put(
        `http://localhost:5144/User/Update?id=${updatedUser.userId}`,
        updatedUser,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      setUser(updatedUser);
      setEditMode(false);
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Failed to update profile.');
    }
  };

  const handleDelete = async () => {
    const confirmDelete = window.confirm('Are you sure you want to delete your account?');
    if (!confirmDelete) return;

    const token = localStorage.getItem('token');
    try {
      await axios.delete(`http://localhost:5144/User/Delete?id=${user.userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      alert('Account deleted successfully.');
      logout();
    } catch (error) {
      console.error('Error deleting account:', error);
      alert('Failed to delete account.');
    }
  };

  const handleLogout = () => {
    logout();
  };

  const handleCartClick = () => {
    setShowCart(true);
    setShowProfile(false);
  };

  return (
    <div className="container-fluid mt-3">
      {/* Header Bar */}
      <div className="border border-2 p-4 mb-4">
        <div className="d-flex justify-content-between align-items-center">
          <h2>Welcome to the Dashboard</h2>
          <div>
            <button className="btn btn-outline-primary me-2" onClick={handleProfileClick}>
              My Profile
            </button>
            <button className="btn btn-outline-success me-2" onClick={handleCartClick}>
              Cart ({cartItems.length})
            </button>
            <button className="btn btn-outline-danger" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </div>

      {/* Profile Section */}
      {showProfile && user && (
        <div className="card p-3 shadow mb-4 position-relative">
          <button
            className="btn-close position-absolute top-0 end-0 m-3"
            onClick={() => setShowProfile(false)}
            aria-label="Close"
          ></button>
          <h3>My Profile</h3>

          {!editMode ? (
            <>
              <p><strong>Name:</strong> {user.name}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Role:</strong> {user.role}</p>

              {user.shippingAddress && <p><strong>Shipping Address:</strong> {user.shippingAddress}</p>}
              {user.paymentDetails && <p><strong>Payment Details:</strong> {user.paymentDetails}</p>}
              {user.permissions && <p><strong>Permissions:</strong> {user.permissions}</p>}

              <div className="mt-3">
                <button className="btn btn-warning me-2" onClick={() => setEditMode(true)}>
                  Update Profile
                </button>
                <button className="btn btn-outline-danger" onClick={handleDelete}>
                  Delete Account
                </button>
              </div>
            </>
          ) : (
            <>
              {/* Editable fields */}
              <div className="mb-2">
                <label><strong>Name:</strong></label>
                <input className="form-control" type="text" value={updatedUser.name}
                  onChange={(e) => setUpdatedUser({ ...updatedUser, name: e.target.value })} />
              </div>
              <div className="mb-2">
                <label><strong>Email:</strong></label>
                <input className="form-control" type="email" value={updatedUser.email}
                  onChange={(e) => setUpdatedUser({ ...updatedUser, email: e.target.value })} />
              </div>
              {updatedUser.shippingAddress !== undefined && (
                <div className="mb-2">
                  <label><strong>Shipping Address:</strong></label>
                  <input className="form-control" type="text" value={updatedUser.shippingAddress}
                    onChange={(e) => setUpdatedUser({ ...updatedUser, shippingAddress: e.target.value })} />
                </div>
              )}
              {updatedUser.paymentDetails !== undefined && (
                <div className="mb-2">
                  <label><strong>Payment Details:</strong></label>
                  <input className="form-control" type="text" value={updatedUser.paymentDetails}
                    onChange={(e) => setUpdatedUser({ ...updatedUser, paymentDetails: e.target.value })} />
                </div>
              )}
              <div className="mb-2">
                <label><strong>Role:</strong></label>
                <input className="form-control" type="text" value={updatedUser.role} readOnly />
              </div>
              {updatedUser.permissions && (
                <div className="mb-2">
                  <label><strong>Permissions:</strong></label>
                  <input className="form-control" type="text" value={updatedUser.permissions} readOnly />
                </div>
              )}
              <div className="mt-3">
                <button className="btn btn-success me-2" onClick={handleUpdate}>Save Changes</button>
                <button className="btn btn-secondary" onClick={() => {
                  setEditMode(false);
                  setUpdatedUser(user);
                }}>Cancel</button>
              </div>
            </>
          )}
        </div>
      )}

      {/* Cart Section */}
      {showCart && (
        <div className="card p-3 shadow mb-4">
          <h3>Your Cart</h3>
          {cartItems.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <ul className="list-group">
              {cartItems.map((item, index) => (
                <li key={index} className="list-group-item">
                  <div className="d-flex justify-content-between align-items-start flex-column">
                    <div className="fw-bold">{item.productName}</div>
                    <div className="text-muted">{item.description}</div>
                    <div className="text-success mt-2">Rs {item.price}</div>
                    <button className="btn btn-sm btn-danger mt-2"
                      onClick={() => handleRemoveFromCart(index)}>
                      Remove
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}

      {/* Categories with cart handler */}
      <Categories onAddToCart={handleAddToCart} />
    </div>
  );
}

export default Welcome;
