// src/pages/Welcome.js
import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from "../components/cards/Categories";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Welcome({ logout }) {
  const [showProfile, setShowProfile] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [user, setUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState(null);
  const [showCart, setShowCart] = useState(false);
  const [cartItems, setCartItems] = useState([]);

  const navigate = useNavigate();

  // Load cart from localStorage on mount
  useEffect(() => {
    const savedCart = localStorage.getItem("cartItems");
    if (savedCart) {
      setCartItems(JSON.parse(savedCart));
    }
  }, []);

  // Load user profile and user-specific cart
  useEffect(() => {
    const token = localStorage.getItem("token");
    const email = localStorage.getItem("email");

    if (!token || !email) {
      navigate("/login");
      return;
    }

    try {
      const tokenData = JSON.parse(atob(token.split(".")[1]));
      const role = tokenData.role || "User";

      const endpoint =
        role === "Admin"
          ? `http://localhost:5144/Admin/Profile?email=${email}`
          : `http://localhost:5144/User/Profile?email=${email}`;

      axios
        .get(endpoint, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setUser(response.data);
          setUpdatedUser(response.data);

          // Load user-specific cart (optional)
          const savedUserCart = localStorage.getItem(`cartItems_${response.data.userId}`);
          if (savedUserCart) {
            setCartItems(JSON.parse(savedUserCart));
          }
        })
        .catch((err) => {
          console.error("Error fetching profile on load:", err);
          navigate("/login");
        });
    } catch (err) {
      console.error("Error parsing token on load:", err);
      logout();
    }
  }, [navigate, logout]);

  // Save cart to localStorage on cartItems change
  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
    if (user && user.userId) {
      localStorage.setItem(`cartItems_${user.userId}`, JSON.stringify(cartItems));
    }
  }, [cartItems, user]);

  // Add product to cart or increase quantity if already exists
  const handleAddToCart = (product) => {
    setCartItems((prev) => {
      const existing = prev.find(
        (item) => item.productName === product.productName
      );
      if (existing) {
        return prev.map((item) =>
          item.productName === product.productName
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  // Remove product or decrease quantity
  const handleRemoveFromCart = (index) => {
    setCartItems((prev) => {
      const updated = [...prev];
      if (updated[index].quantity > 1) {
        updated[index].quantity -= 1;
      } else {
        updated.splice(index, 1);
      }
      return updated;
    });
  };

  // Profile update
  const handleUpdate = async () => {
    const token = localStorage.getItem("token");
    try {
      await axios.put(
        `http://localhost:5144/User/Update?id=${updatedUser.userId}`,
        updatedUser,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      setUser(updatedUser);
      setEditMode(false);
      alert("Profile updated successfully!");
    } catch (error) {
      console.error("Error updating profile:", error);
      alert("Failed to update profile.");
    }
  };

  // Profile delete
  const handleDelete = async () => {
    const confirmDelete = window.confirm(
      "Are you sure you want to delete your account?"
    );
    if (!confirmDelete) return;

    const token = localStorage.getItem("token");
    try {
      await axios.delete(`http://localhost:5144/User/Delete?id=${user.userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      alert("Account deleted successfully.");
      localStorage.removeItem(`cartItems_${user.userId}`);
      logout();
    } catch (error) {
      console.error("Error deleting account:", error);
      alert("Failed to delete account.");
    }
  };

  const handleLogout = () => {
    setCartItems([]);
    setUser(null);
    setShowProfile(false);
    setShowCart(false);
    localStorage.removeItem("cartItems");
    logout();
  };

  const handleProfileClick = () => {
    setShowProfile(true);
    setShowCart(false);
  };

  const handleCartClick = () => {
    setShowCart(true);
    setShowProfile(false);
  };

  return (
    <div className="container-fluid mt-3">
      {/* Header Bar */}
      <div className="border border-2 p-4 mb-4">
        <div className="d-flex justify-content-between align-items-center">
          <h2>Welcome to the Dashboard</h2>
          <div>
            <button className="btn btn-outline-primary me-2" onClick={handleProfileClick}>
              My Profile
            </button>
            <button className="btn btn-outline-success me-2" onClick={handleCartClick}>
              Cart ({cartItems.reduce((acc, item) => acc + item.quantity, 0)})
            </button>
            <button className="btn btn-outline-danger" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </div>

      {/* Profile Section */}
      {showProfile && user && (
        <div className="card p-3 shadow mb-4 position-relative">
          <button
            className="btn-close position-absolute top-0 end-0 m-3"
            onClick={() => setShowProfile(false)}
            aria-label="Close"
          ></button>
          <h3>My Profile</h3>

          {!editMode ? (
            <>
              <p><strong>Name:</strong> {user.name}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Role:</strong> {user.role}</p>

              {user.shippingAddress && <p><strong>Shipping Address:</strong> {user.shippingAddress}</p>}
              {user.paymentDetails && <p><strong>Payment Details:</strong> {user.paymentDetails}</p>}
              {user.permissions && <p><strong>Permissions:</strong> {user.permissions}</p>}

              <div className="mt-3">
                <button className="btn btn-warning me-2" onClick={() => setEditMode(true)}>
                  Update Profile
                </button>
                <button className="btn btn-outline-danger" onClick={handleDelete}>
                  Delete Account
                </button>
              </div>
            </>
          ) : (
            <>
              <div className="mb-2">
                <label><strong>Name:</strong></label>
                <input
                  className="form-control"
                  type="text"
                  value={updatedUser.name}
                  onChange={(e) => setUpdatedUser({ ...updatedUser, name: e.target.value })}
                />
              </div>
              <div className="mb-2">
                <label><strong>Email:</strong></label>
                <input
                  className="form-control"
                  type="email"
                  value={updatedUser.email}
                  onChange={(e) => setUpdatedUser({ ...updatedUser, email: e.target.value })}
                />
              </div>
              {updatedUser.shippingAddress !== undefined && (
                <div className="mb-2">
                  <label><strong>Shipping Address:</strong></label>
                  <input
                    className="form-control"
                    type="text"
                    value={updatedUser.shippingAddress}
                    onChange={(e) => setUpdatedUser({ ...updatedUser, shippingAddress: e.target.value })}
                  />
                </div>
              )}
              {updatedUser.paymentDetails !== undefined && (
                <div className="mb-2">
                  <label><strong>Payment Details:</strong></label>
                  <input
                    className="form-control"
                    type="text"
                    value={updatedUser.paymentDetails}
                    onChange={(e) => setUpdatedUser({ ...updatedUser, paymentDetails: e.target.value })}
                  />
                </div>
              )}
              <div className="mb-2">
                <label><strong>Role:</strong></label>
                <input className="form-control" type="text" value={updatedUser.role} readOnly />
              </div>
              {updatedUser.permissions && (
                <div className="mb-2">
                  <label><strong>Permissions:</strong></label>
                  <input className="form-control" type="text" value={updatedUser.permissions} readOnly />
                </div>
              )}
              <div className="mt-3">
                <button className="btn btn-success me-2" onClick={handleUpdate}>Save Changes</button>
                <button
                  className="btn btn-secondary"
                  onClick={() => {
                    setEditMode(false);
                    setUpdatedUser(user);
                  }}
                >
                  Cancel
                </button>
              </div>
            </>
          )}
        </div>
      )}

      {/* Cart Section */}
      {showCart && (
        <div className="card p-3 shadow mb-4">
          <h3>Your Cart</h3>
          {cartItems.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <ul className="list-group">
              {cartItems.map((item, index) => (
                <li key={index} className="list-group-item d-flex justify-content-between align-items-start flex-column">
                  <div>
                    <strong>{item.productName}</strong>
                    <p className="mb-1 text-muted">{item.description}</p>
                    <span className="text-success">Rs {item.price}</span>
                    <p>Quantity: {item.quantity}</p>
                  </div>
                  <button
                    className="btn btn-sm btn-danger mt-2"
                    onClick={() => {
                      // Remove one quantity or remove item completely
                      if (item.quantity > 1) {
                        setCartItems((prev) =>
                          prev.map((ci, i) =>
                            i === index ? { ...ci, quantity: ci.quantity - 1 } : ci
                          )
                        );
                      } else {
                        setCartItems((prev) => prev.filter((_, i) => i !== index));
                      }
                    }}
                  >
                    Remove
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}

      {/* Categories component with addToCart handler */}
      <Categories onAddToCart={handleAddToCart} />
    </div>
  );
}

export default Welcome;
