import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductService from '../../services/ProductService'; // ✅ Fix the path

function Categories() {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get("http://localhost:5144/Category", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCategories(response.data);
      } catch (error) {
        console.error("Failed to fetch categories:", error);
      }
    };

    fetchCategories();
  }, []);

  const handleCategoryClick = async (categoryName) => {
    try {
      const response = await ProductService.getProductsByCategory(categoryName);
      setSelectedCategory(categoryName);
      setProducts(response.data);
    } catch (error) {
      console.error("Failed to fetch products by category:", error);
    }
  };

  return (
    <div className="container mt-4">
      <h4>Select Category</h4>
      <div className="row">
        {categories.length > 0 ? (
          categories.map((category) => (
            <div
              className="col-md-3 mb-3"
              key={category.categoryId}
              onClick={() => handleCategoryClick(category.name)}
              style={{ cursor: "pointer" }}
            >
              <div className="card shadow-sm">
                <div className="card-body text-center">
                  <h5 className="card-title">{category.name}</h5>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No categories found.</p>
        )}
      </div>

      {/* Show Products in Selected Category */}
      {selectedCategory && (
        <div className="mt-4">
          <h5>Products in "{selectedCategory}"</h5>
          <div className="row">
            {products.length > 0 ? (
              products.map((product) => (
                <div className="col-md-4 mb-3" key={product.productId}>
                  <div className="card">
                    <div className="card-body">
                      <h6 className="card-title">{product.productName}</h6>
                      <p>{product.description}</p>
                      <p><strong>${product.price}</strong></p>
                      {product.imageURL && (
                        <img
                          src={product.imageURL}
                          alt={product.productName}
                          className="img-fluid"
                        />
                      )}
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <p>No products found in this category.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Categories;
