import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Welcome() {
  const navigate = useNavigate();
  const email = localStorage.getItem("email");
  const role = localStorage.getItem("role");

  const [profile, setProfile] = useState(null);
  const [error, setError] = useState("");

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  const handleHome = () => {
    navigate("/");
  };

  useEffect(() => {
    if (!email) {
      console.warn("Email not found in localStorage");
      return;
    }

    const url =
      role === "Admin"
        ? `http://localhost:5144/Admin/Profile?email=${email}`
        : `http://localhost:5144/User/Profile?email=${email}`;

    axios
      .get(url)
      .then((res) => {
        setProfile(res.data);
      })
      .catch((err) => {
        console.error("Profile fetch failed:", err);
        setError("Unable to fetch profile.");
      });
  }, [email, role]);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Welcome {role}</h1>

      <div style={{ marginTop: "20px" }}>
        <button onClick={handleHome} style={{ marginRight: "10px" }}>
          Home
        </button>
        <button onClick={handleLogout}>Logout</button>
      </div>

      <hr />

      {/* Profile Section */}
      <div style={{ marginTop: "30px" }}>
        <h2>Profile Information</h2>

        {error && <p style={{ color: "red" }}>{error}</p>}

        {profile ? (
          <table border="1" cellPadding="10" style={{ marginTop: "10px" }}>
            <tbody>
              <tr>
                <td><strong>Name</strong></td>
                <td>{profile.name || "N/A"}</td>
              </tr>
              <tr>
                <td><strong>Email</strong></td>
                <td>{profile.email}</td>
              </tr>
              <tr>
                <td><strong>Role</strong></td>
                <td>{profile.role}</td>
              </tr>
              {/* You can add more profile fields here if needed */}
            </tbody>
          </table>
        ) : (
          !error && <p>Loading profile...</p>
        )}
      </div>
    </div>
  );
}

export default Welcome;
