import React, { useState, useEffect } from 'react';
import CategoryService from '../services/CategoryService';
import ProductService from '../../../services/ProductService';

function Categories() {
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await CategoryService.getCategories();
        setCategories(response.data);
      } catch (error) {
        console.error("Failed to fetch categories:", error);
      }
    };

    fetchCategories();
  }, []);

  const handleCategoryClick = async (categoryName) => {
    setSelectedCategory(categoryName);
    try {
      const response = await ProductService.getProductsByCategory(categoryName);
      setProducts(response.data);
    } catch (error) {
      console.error("Failed to fetch products by category:", error);
      setProducts([]);
    }
  };

  return (
    <div className="container mt-4">
      <h4>Categories</h4>
      <div className="row mb-4">
        {categories.length > 0 ? (
          categories.map((category) => (
            <div className="col-md-3 mb-3" key={category.categoryId}>
              <button
                className={`btn btn-outline-primary w-100 ${selectedCategory === category.name ? 'active' : ''}`}
                onClick={() => handleCategoryClick(category.name)}
              >
                {category.name}
              </button>
            </div>
          ))
        ) : (
          <p>No categories found.</p>
        )}
      </div>

      {selectedCategory && (
        <>
          <h5>Products in "{selectedCategory}"</h5>
          <div className="row">
            {products.length > 0 ? (
              products.map((product) => (
                <div className="col-md-4 mb-3" key={product.productId}>
                  <div className="card shadow-sm">
                    <div className="card-body">
                      <h6>{product.productName}</h6>
                      <p>{product.description}</p>
                      <p><b>Price: </b>${product.price}</p>
                      {product.imageURL && (
                        <img
                          src={product.imageURL}
                          alt={product.productName}
                          style={{ maxWidth: '100%', height: '150px', objectFit: 'cover' }}
                        />
                      )}
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <p>No products found in this category.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default Categories;
