import React, { useState, useEffect } from "react";
import axios from "axios";

const API_BASE = "http://localhost:5000"; // Change to your actual API base URL

const getToken = () => localStorage.getItem("token");

const getConfig = () => ({
  headers: { Authorization: `Bearer ${getToken()}` },
});

function AdminPanel() {
  const [admins, setAdmins] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [form, setForm] = useState({
    AdminId: 0,
    Name: "",
    Role: "",
    Permissions: "",
    Email: "",
    Password: "",
  });
  const [isEditMode, setIsEditMode] = useState(false);

  // Fetch all admins
  const fetchAdmins = async () => {
    setLoading(true);
    setError("");
    try {
      const res = await axios.get(`${API_BASE}/Admin/ListofAdmins`, getConfig());
      setAdmins(res.data);
    } catch (err) {
      setError("Failed to fetch admins");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAdmins();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Reset form to empty
  const resetForm = () => {
    setForm({
      AdminId: 0,
      Name: "",
      Role: "",
      Permissions: "",
      Email: "",
      Password: "",
    });
    setIsEditMode(false);
  };

  // Add new admin
  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_BASE}/Admin/CreateAdmin`, form, getConfig());
      fetchAdmins();
      resetForm();
    } catch (err) {
      setError("Failed to add admin");
      console.error(err);
    }
  };

  // Edit admin (load admin data into form)
  const handleEditClick = (admin) => {
    setForm(admin);
    setIsEditMode(true);
  };

  // Update admin
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_BASE}/Admin/Update?id=${form.AdminId}`, form, getConfig());
      fetchAdmins();
      resetForm();
    } catch (err) {
      setError("Failed to update admin");
      console.error(err);
    }
  };

  // Delete admin
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this admin?")) return;

    try {
      await axios.delete(`${API_BASE}/Admin/Delete?id=${id}`, getConfig());
      fetchAdmins();
    } catch (err) {
      setError("Failed to delete admin");
      console.error(err);
    }
  };

  return (
    <div style={{ maxWidth: 900, margin: "20px auto", padding: 20, border: "1px solid #ccc" }}>
      <h2>Admin Management Panel</h2>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <form onSubmit={isEditMode ? handleUpdate : handleAdd} style={{ marginBottom: 20 }}>
        <div>
          <label>Name:</label>
          <input
            name="Name"
            value={form.Name}
            onChange={handleChange}
            required
            placeholder="Name"
          />
        </div>
        <div>
          <label>Role:</label>
          <input
            name="Role"
            value={form.Role}
            onChange={handleChange}
            required
            placeholder="Role"
          />
        </div>
        <div>
          <label>Permissions:</label>
          <input
            name="Permissions"
            value={form.Permissions}
            onChange={handleChange}
            placeholder="Permissions"
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            name="Email"
            type="email"
            value={form.Email}
            onChange={handleChange}
            required
            placeholder="Email"
            disabled={isEditMode} // Prevent editing email on update (optional)
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            name="Password"
            type="password"
            value={form.Password}
            onChange={handleChange}
            required={!isEditMode} // Required on add, optional on edit
            placeholder="Password"
          />
        </div>

        <button type="submit" style={{ marginRight: 10 }}>
          {isEditMode ? "Update Admin" : "Add Admin"}
        </button>
        {isEditMode && (
          <button type="button" onClick={resetForm}>
            Cancel
          </button>
        )}
      </form>

      <h3>List of Admins</h3>

      {loading ? (
        <p>Loading admins...</p>
      ) : admins.length === 0 ? (
        <p>No admins found.</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0" style={{ width: "100%" }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Role</th>
              <th>Permissions</th>
              <th>Email</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {admins.map((admin) => (
              <tr key={admin.adminId || admin.AdminId}>
                <td>{admin.name || admin.Name}</td>
                <td>{admin.role || admin.Role}</td>
                <td>{admin.permissions || admin.Permissions}</td>
                <td>{admin.email || admin.Email}</td>
                <td>
                  <button onClick={() => handleEditClick(admin)} style={{ marginRight: 5 }}>
                    Edit
                  </button>
                  <button onClick={() => handleDelete(admin.adminId || admin.AdminId)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AdminPanel;
