import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Welcome() {
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState('');

  const email = localStorage.getItem('email');
  const role = localStorage.getItem('role');
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!email || !role || !token) {
      setError('Missing login credentials. Please login again.');
      return;
    }

    const fetchProfile = async () => {
      try {
        const url =
          role === 'Admin'
            ? `http://localhost:5144/Admin/Profile?email=${email}`
            : `http://localhost:5144/User/Profile?email=${email}`;

        const response = await axios.get(url, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setProfile(response.data);
      } catch (err) {
        console.error('Profile fetch failed:', err);
        setError('Failed to fetch profile.');
      }
    };

    fetchProfile();
  }, [email, role, token]);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Welcome Page</h1>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Profile Section */}
      {profile && (
        <div style={{ marginTop: '2rem', border: '1px solid #ccc', padding: '1rem', width: '400px' }}>
          <h2>Profile</h2>
          <p><strong>Name:</strong> {profile.name || profile.userName}</p>
          <p><strong>Email:</strong> {profile.email}</p>
          {role === 'Admin' ? (
            <>
              <p><strong>Admin ID:</strong> {profile.adminId}</p>
            </>
          ) : (
            <>
              <p><strong>User ID:</strong> {profile.userId}</p>
            </>
          )}
        </div>
      )}

      {/* Logout */}
      <button onClick={handleLogout} style={{ marginTop: '2rem' }}>
        Logout
      </button>
    </div>
  );
}

export default Welcome;
