// AdminPanel.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const API_BASE = "http://localhost:5144"; // Backend base URL

export default function AdminPanel() {
  const [admins, setAdmins] = useState([]);
  const [formAdmin, setFormAdmin] = useState({
    name: "",
    role: "",
    permissions: "",
    email: "",
    password: "",
  });
  const [updateId, setUpdateId] = useState(null);
  const [email, setEmail] = useState(""); // Login email
  const [password, setPassword] = useState(""); // Login password
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const getToken = () => localStorage.getItem("token");

  const getConfig = () => ({
    headers: { Authorization: `Bearer ${getToken()}` },
  });

  // Fetch admins from backend
  const fetchAdmins = async () => {
    setLoading(true);
    setErrorMessage("");
    try {
      const res = await axios.get(`${API_BASE}/Admin/ListofAdmins`, getConfig());
      setAdmins(res.data);
    } catch (err) {
      console.error("Fetch admins error:", err.response || err);
      if (err.response?.status === 401) {
        setErrorMessage("Unauthorized. Please log in as Admin.");
        setIsLoggedIn(false);
      } else {
        setErrorMessage("Failed to fetch admins.");
      }
    } finally {
      setLoading(false);
    }
  };

  // Login handler
  const handleLogin = async () => {
    setErrorMessage("");
    try {
      const res = await axios.post(`${API_BASE}/Auth/login`, { email, password });
      const token = res.data?.token || res.data?.Token; // Handle different casing
      if (!token) throw new Error("Token not found in response");

      localStorage.setItem("token", token);
      setIsLoggedIn(true);
      fetchAdmins();
    } catch (err) {
      console.error("Login error:", err.response || err);
      setErrorMessage("Login failed. Check your credentials.");
    }
  };

  // Create or update admin
  const handleSave = async () => {
    if (!formAdmin.name || !formAdmin.email) {
      setErrorMessage("Name and Email are required.");
      return;
    }

    try {
      if (updateId) {
        await axios.put(`${API_BASE}/Admin/Update?id=${updateId}`, formAdmin, getConfig());
      } else {
        await axios.post(`${API_BASE}/Admin/CreateAdmin`, formAdmin, getConfig());
      }
      resetForm();
      fetchAdmins();
    } catch (err) {
      console.error("Save error:", err.response || err);
      setErrorMessage("Failed to save admin.");
    }
  };

  // Delete admin
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this admin?")) return;

    try {
      await axios.delete(`${API_BASE}/Admin/Delete?id=${id}`, getConfig());
      fetchAdmins();
    } catch (err) {
      console.error("Delete error:", err.response || err);
      setErrorMessage("Failed to delete admin.");
    }
  };

  // Edit admin (fill form)
  const handleEdit = (admin) => {
    setFormAdmin({
      name: admin.name,
      role: admin.role,
      permissions: admin.permissions,
      email: admin.email,
      password: admin.password || "",
    });
    setUpdateId(admin.adminId);
  };

  // Reset form
  const resetForm = () => {
    setFormAdmin({ name: "", role: "", permissions: "", email: "", password: "" });
    setUpdateId(null);
  };

  // Auto-check token on page load
  useEffect(() => {
    if (getToken()) {
      setIsLoggedIn(true);
      fetchAdmins();
    }
  }, []);

  if (!isLoggedIn) {
    return (
      <div style={{ padding: "20px" }}>
        <h2>Admin Login</h2>
        {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
        <input
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />{" "}
        <br />
        <input
          placeholder="Password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />{" "}
        <br />
        <button onClick={handleLogin}>Login</button>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2>Admin Panel</h2>
      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          {/* Admin Form */}
          <div>
            <input
              placeholder="Name"
              value={formAdmin.name}
              onChange={(e) => setFormAdmin({ ...formAdmin, name: e.target.value })}
            />{" "}
            <input
              placeholder="Role"
              value={formAdmin.role}
              onChange={(e) => setFormAdmin({ ...formAdmin, role: e.target.value })}
            />{" "}
            <input
              placeholder="Permissions"
              value={formAdmin.permissions}
              onChange={(e) => setFormAdmin({ ...formAdmin, permissions: e.target.value })}
            />{" "}
            <input
              placeholder="Email"
              value={formAdmin.email}
              onChange={(e) => setFormAdmin({ ...formAdmin, email: e.target.value })}
            />{" "}
            <input
              placeholder="Password"
              type="password"
              value={formAdmin.password}
              onChange={(e) => setFormAdmin({ ...formAdmin, password: e.target.value })}
            />{" "}
            <button onClick={handleSave}>{updateId ? "Update" : "Create"}</button>
            {updateId && <button onClick={resetForm}>Cancel</button>}
          </div>

          {/* Admin List */}
          <table border="1" cellPadding="10" style={{ marginTop: "20px" }}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Role</th>
                <th>Permissions</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {admins.length > 0 ? (
                admins.map((admin) => (
                  <tr key={admin.adminId}>
                    <td>{admin.adminId}</td>
                    <td>{admin.name}</td>
                    <td>{admin.role}</td>
                    <td>{admin.permissions}</td>
                    <td>{admin.email}</td>
                    <td>
                      <button onClick={() => handleEdit(admin)}>Edit</button>
                      <button onClick={() => handleDelete(admin.adminId)}>Delete</button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" style={{ textAlign: "center" }}>
                    No admins found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}
