import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Categories from "../components/cards/Categories";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

function Welcome({ logout }) {
  const [user, setUser] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [showProfile, setShowProfile] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [updatedUser, setUpdatedUser] = useState(null);
  const [cartLoaded, setCartLoaded] = useState(false);
  const [userRole, setUserRole] = useState(null);

  const navigate = useNavigate();

  // Load user & cart once
  useEffect(() => {
    const token = localStorage.getItem("token");
    const email = localStorage.getItem("email");

    if (token) {
      try {
        const tokenData = JSON.parse(atob(token.split(".")[1]));
        setUserRole(tokenData.role || "User"); // store role for quick access
      } catch {
        logout();
      }
    }

    if (!token || !email) {
      // Guest cart
      const tempCart = localStorage.getItem("tempCart");
      if (tempCart) {
        setCartItems(JSON.parse(tempCart));
      }
      setCartLoaded(true);
      return;
    }

    try {
      const tokenData = JSON.parse(atob(token.split(".")[1]));
      const role = tokenData.role || "User";
      const endpoint =
        role === "Admin"
          ? `http://localhost:5144/Admin/Profile?email=${email}`
          : `http://localhost:5144/User/Profile?email=${email}`;

      axios
        .get(endpoint, { headers: { Authorization: `Bearer ${token}` } })
        .then((res) => {
          const fetchedUser = res.data;
          setUser(fetchedUser);
          setUpdatedUser(fetchedUser);

          const userCartKey = `cartItems_${fetchedUser.userId}`;
          const savedUserCart = localStorage.getItem(userCartKey);
          const tempCart = localStorage.getItem("tempCart");

          if (savedUserCart && JSON.parse(savedUserCart).length > 0) {
            setCartItems(JSON.parse(savedUserCart));
          } else if (tempCart && JSON.parse(tempCart).length > 0) {
            setCartItems(JSON.parse(tempCart));
            localStorage.setItem(userCartKey, tempCart);
          }
          setCartLoaded(true);
        })
        .catch(() => navigate("/login"));
    } catch {
      logout();
    }
  }, [navigate, logout]);

  // Save cart AFTER it has been loaded
  useEffect(() => {
    if (!cartLoaded) return;

    localStorage.setItem("tempCart", JSON.stringify(cartItems));
    if (user?.userId) {
      localStorage.setItem(`cartItems_${user.userId}`, JSON.stringify(cartItems));
    }
  }, [cartItems, user, cartLoaded]);

  // Updated handleAddToCart: calls backend, then updates local state
  const handleAddToCart = async (product) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You must login to add to cart");
        return;
      }

      await axios.post(
        `http://localhost:5144/ShoppingCart/AddToCart?productId=${product.productId}`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setCartItems((prev) => [...prev, product]);
    } catch (error) {
      console.error("Failed to add product to cart:", error);
      alert("Failed to add product to cart. Please try again.");
    }
  };

  const handleRemoveFromCart = (index) => {
    setCartItems((prev) => prev.filter((_, i) => i !== index));
  };

  return (
    <div className="container-fluid mt-3">
      <div className="border border-2 p-4 mb-4 d-flex justify-content-between">
        <h2>Welcome to the Dashboard</h2>
        <div>
          {/* Show Admin Panel only if Admin */}
          {(user?.role === "Admin" || userRole === "Admin") && (
            <Link to="/adminpanel" className="btn btn-outline-dark me-2">
              Admin Panel
            </Link>
          )}
          <button className="btn btn-outline-primary me-2" onClick={() => setShowProfile(true)}>
            My Profile
          </button>
          <button className="btn btn-outline-success me-2" onClick={() => setShowCart(true)}>
            Cart ({cartItems.length})
          </button>
          <button className="btn btn-outline-danger" onClick={logout}>
            Logout
          </button>
        </div>
      </div>

      {showCart && (
        <div className="card p-3 shadow mb-4">
          <h3>Your Cart</h3>
          {cartItems.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <ul className="list-group">
              {cartItems.map((item, i) => (
                <li key={i} className="list-group-item">
                  <strong>{item.productName}</strong> - Rs {item.price}
                  <p className="mb-1">{item.description}</p>
                  <button
                    className="btn btn-sm btn-danger float-end"
                    onClick={() => handleRemoveFromCart(i)}
                  >
                    Remove
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}

      <Categories onAddToCart={handleAddToCart} />
    </div>
  );
}

export default Welcome;
