import axios from 'axios';
import AuthService from './AuthService';

const API_URL = 'http://localhost:5144/Product';
const CATEGORY_API_URL = 'http://localhost:5144/Category';

class ProductService {
  getHeaders() {
    const token = AuthService.getToken();
    return {
      headers: { Authorization: `Bearer ${token}` },
    };
  }

  getProducts() {
    return axios.get(API_URL, this.getHeaders());
  }

  getProduct(id) {
    return axios.get(`${API_URL}/${id}`, this.getHeaders());
  }

  addProduct(product) {
    return axios.post(`${API_URL}/AddNewProduct`, product, this.getHeaders());
  }

  updateProduct(id, product) {
    return axios.put(`${API_URL}/UpdateProduct?id=${id}`, product, this.getHeaders());
  }

  deleteProduct(id) {
    return axios.delete(`${API_URL}/DeleteProduct?id=${id}`, this.getHeaders());
  }

  getProductsByCategory(category) {
    return axios.get(`${API_URL}/ByCategory`, {
      params: { category },
      ...this.getHeaders(),
    });
  }

  getCategories() {
    return axios.get(CATEGORY_API_URL, this.getHeaders());
  }
}

export default new ProductService();
