import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';

function CategoryService() {
  const [categories, setCategories] = useState([]);
  const [newCategory, setNewCategory] = useState({ name: '' });
  const [editCategoryId, setEditCategoryId] = useState(null);
  const [editCategoryData, setEditCategoryData] = useState({});
  const [message, setMessage] = useState('');

  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  const fetchCategories = async () => {
    try {
      const response = await axios.get('http://localhost:5144/Category', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setCategories(response.data);
    } catch (error) {
      console.error("Error fetching categories:", error);
      alert("Failed to fetch category data");
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  const handleAddCategory = async () => {
    if (!newCategory.name) {
      alert("Please fill in the category name.");
      return;
    }

    try {
      const payload = { name: newCategory.name };

      await axios.post('http://localhost:5144/Category/AddingNewCategory', payload, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessage("Category added successfully");
      setNewCategory({ name: '' });
      fetchCategories();
    } catch (error) {
      alert("Failed to add category. Check permissions.");
    }
  };

  const handleEdit = (category) => {
    setEditCategoryId(category.categoryId);
    setEditCategoryData({ ...category });
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`http://localhost:5144/Category/UpdateCategoryid=${editCategoryId}`, null, {
        params: {
          id: editCategoryId,
          name: editCategoryData.name
        },
        headers: { Authorization: `Bearer ${token}` }
      });

      setEditCategoryId(null);
      setEditCategoryData({});
      fetchCategories();
      setMessage("Category updated successfully");
    } catch (error) {
      alert("Update failed.");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this category?")) return;

    try {
      await axios.delete(`http://localhost:5144/Category/DeleteCategory?id=${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      fetchCategories();
      setMessage("Category deleted successfully");
    } catch (error) {
      alert("Delete failed.");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">E-commerce Category Management</h2>

      {/* Add Category */}
      <div className="mb-4">
        <h5>Add New Category</h5>
        <div className="row g-2 align-items-center">
          <div className="col-md-4">
            <input
              type="text"
              className="form-control"
              placeholder="Category Name (e.g., Women)"
              value={newCategory.name}
              onChange={(e) => setNewCategory({ ...newCategory, name: e.target.value })}
            />
          </div>
          <div className="col-md-2">
            <button className="btn btn-success w-100" onClick={handleAddCategory}>Add</button>
          </div>
        </div>
      </div>

      {/* Category Buttons */}
      <h5 className="mt-4">Existing Categories</h5>
      <div className="d-flex flex-wrap gap-2">
        {categories.length === 0 ? (
          <span>No categories available.</span>
        ) : (
          categories.map((category) => (
            <div key={category.categoryId} className="d-flex align-items-center gap-1">
              {editCategoryId === category.categoryId ? (
                <>
                  <input
                    className="form-control form-control-sm"
                    value={editCategoryData.name}
                    onChange={(e) => setEditCategoryData({ ...editCategoryData, name: e.target.value })}
                    style={{ width: '0px' }}
                  />
                  <button className="btn btn-sm btn-warning" onClick={handleUpdate}>Save</button>
                </>
              ) : (
                <>
                  <button
                    className="btn btn-outline-primary btn-sm"
                    style={{ minWidth: '50px', fontSize: '13px' }}
                  >
                    {category.name}
                  </button>
                  <button
                    className="btn btn-sm btn-secondary"
                    onClick={() => handleEdit(category)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-sm btn-danger"
                    onClick={() => handleDelete(category.categoryId)}
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
          ))
        )}
      </div>

      {/* Show items in horizontal scrollable row */}
      {categories.some(cat => cat.items && cat.items.length > 0) && (
        <>
          <h5 className="mt-5">All Items</h5>
          <div
            style={{
              display: 'flex',
              overflowX: 'auto',
              whiteSpace: 'nowrap',
              padding: '10px',
              gap: '15px',
              scrollSnapType: 'x mandatory'
            }}
          >
            {categories.flatMap(cat =>
              (cat.items || []).map((item, index) => (
                <div
                  key={`${cat.categoryId}-${index}`}
                  style={{
                    minWidth: '120px',
                    padding: '10px',
                    backgroundColor: '#f1f1f1',
                    border: '1px solid #ccc',
                    borderRadius: '6px',
                    textAlign: 'center',
                    flexShrink: 0,
                    fontSize: '13px',
                    scrollSnapAlign: 'start',
                  }}
                >
                  <strong>{item.name}</strong>
                  <div style={{ fontSize: '11px', marginTop: '5px' }}>
                    <em>{cat.name}</em>
                  </div>
                </div>
              ))
            )}
          </div>
        </>
      )}

      {message && <div className="alert alert-success mt-3">{message}</div>}
    </div>
  );
}

export default CategoryService;
