import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
  });

  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role'); // 'User' or 'Admin'

  const fetchUser = async () => {
    try {
      const endpoint = role === 'Admin' ? '/Admin/GetById' : '/User/GetById';
      const response = await axios.get(endpoint, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(response.data);
      setFormData({
        name: response.data.name,
        email: response.data.email,
      });
    } catch (error) {
      console.error('Error fetching user:', error);
    }
  };

  useEffect(() => {
    if (showProfile) {
      fetchUser();
    }
  }, [showProfile]);

  const handleEditToggle = () => {
    setEditMode(true);
  };

  const handleSave = async () => {
    try {
      const endpoint = role === 'Admin' ? '/Admin/Update' : '/User/Update';
      await axios.put(endpoint, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setEditMode(false);
      fetchUser(); // Refresh after update
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleLogout = () => {
    localStorage.clear();
    window.location.href = '/';
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between mb-3">
        <button onClick={() => setShowProfile(false)} className="btn btn-success">
          Home
        </button>
        <button onClick={() => setShowProfile(true)} className="btn btn-primary">
          My Profile
        </button>
        <button onClick={handleLogout} className="btn btn-danger">
          Logout
        </button>
      </div>

      {showProfile ? (
        user ? (
          <div className="card p-4 shadow">
            <h2>My Profile</h2>
            <div className="mb-3">
              <label>Name</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="form-control"
                readOnly={!editMode}
              />
            </div>
            <div className="mb-3">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="form-control"
                readOnly={!editMode}
              />
            </div>
            {editMode ? (
              <button className="btn btn-success" onClick={handleSave}>
                Save
              </button>
            ) : (
              <button className="btn btn-warning" onClick={handleEditToggle}>
                Edit
              </button>
            )}
          </div>
        ) : (
          <p>Loading profile...</p>
        )
      ) : (
        <Categories />
      )}
    </div>
  );
}

export default Welcome;
