import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

import Login from './components/Login';
import Register from './components/Register';
import AuthService from './services/AuthService';

import Welcome from './components/Welcome';

import HeaderNavBar from './HeaderNavBar';

import WomenServices from './WomenServices';
import MenServices from './MenServices';
import KidsServices from './KidsServices';

// Optional CategoryService if you want to keep it
import CategoryService from './services/CategoryService';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = AuthService.getToken();
    if (token) {
      const user = AuthService.getUserFromToken(token);
      setUser(user);
    }
  }, []);

  const login = async (email, password) => {
    try {
      const token = await AuthService.login(email, password);
      const user = AuthService.getUserFromToken(token);
      setUser(user);
    } catch (error) {
      console.error('Error logging in', error);
    }
  };

  const logout = () => {
    AuthService.logout();
    setUser(null);
  };

  return (
    <Router>
      {/* Header with login/logout */}
      <header>
        <nav className="d-flex align-items-center justify-content-between px-4 py-2 bg-light">
          {user ? (
            <>
              <span>Welcome, {user.email}</span>
              <div>
                <Link to="/Welcome" className="btn btn-outline-primary mx-2">Home</Link>
                <button onClick={logout} className="btn btn-danger mx-2">Logout</button>
              </div>
            </>
          ) : (
            <span>Not logged in yet!</span>
          )}
        </nav>
      </header>

      {/* Main navigation bar */}
      {user && <HeaderNavBar />}

      <Routes>
        {/* Redirect root to login */}
        <Route path="/" element={<Navigate to="/login" />} />

        {/* Protected routes */}
        <Route path="/Welcome" element={user ? <Welcome /> : <Navigate to="/login" />} />

        <Route path="/services/women" element={user ? <WomenServices /> : <Navigate to="/login" />} />
        <Route path="/services/men" element={user ? <MenServices /> : <Navigate to="/login" />} />
        <Route path="/services/kids" element={user ? <KidsServices /> : <Navigate to="/login" />} />

        {/* Optional CategoryService if still used */}
        <Route path="/services/CategoryService" element={user ? <CategoryService /> : <Navigate to="/login" />} />

        {/* Authentication routes */}
        <Route path="/login" element={!user ? <Login login={login} /> : <Navigate to="/Welcome" />} />
        <Route path="/register" element={<Register />} />

        {/* Add other routes here */}
      </Routes>
    </Router>
  );
}

export default App;
