import React,{useState,useEffect} from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate,Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Login from './components/Login';
import Register from './components/Register';
import AuthService from './services/AuthService';
import FlightService from './services/FlightService';
import Welcome from './components/Welcome';

import CategoryService from './services/CategoryService';
// Import other components like Profile if you have them

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = AuthService.getToken();
    if (token) {
      const user = AuthService.getUserFromToken(token);
      setUser(user);
    }
  }, []);

  const login = async (email, password) => {
    try {
      const token = await AuthService.login(email, password);
      const user = AuthService.getUserFromToken(token);
      setUser(user);
    } catch (error) {
      console.error('Error logging in', error);
    }
  };

  const logout = () => {
    AuthService.logout();
    setUser(null);
  };
  return (

    <Router>
      <header>
          <nav className="d-flex align-items-center justify-content-between px-4 py-2 bg-light">
            {user ? (
              <>
                <span>Welcome, {user.email}</span>
                <div>
                  <Link to="/Welcome" className="btn btn-outline-primary mx-2">Home</Link>

                  <button onClick={logout} className="btn btn-danger mx-2">Logout</button>
                </div>
              </>
            ) : (
              <span>Not logged in yet!</span>
            )}
          </nav>
        </header>

      <Routes>
        <Route path="/" element={<Navigate to="/login" />} />
        <Route path="/Welcome" element={user ? <Welcome /> : <Navigate to="/login" />} />
        <Route path="/login" element={!user ? <Login login={login} /> : <Navigate to="/Welcome"/>}/>
        <Route path="/register" element={<Register />} />

        {/* <Route path="/services/FlightService" element={user ? <FlightService /> : <Navigate to="/login" />} />
        <Route path="/services/PaymentService" element={user ? <PaymentService /> : <Navigate to="/login" />} /> */}
        <Route path="/services/CategoryService" element={user ? <CategoryService/> : <Navigate to="/login" />} />

        {/* Add other routes here */}
      </Routes>
    </Router>
  );
}

export default App;
