import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    contactNumber: ''
  });

  const token = localStorage.getItem('token');

  const fetchUserProfile = async () => {
    if (!token) {
      alert("Token missing. Please login again.");
      return;
    }

    try {
      const response = await axios.get('http://localhost:5144/User/Profile', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      const data = response.data;

      setUser(data);

      setFormData({
        email: data.email || '',
        password: '',
        contactNumber: data.contactNumber || data.phone || ''
      });
    } catch (error) {
      console.error('Error fetching profile:', error);
      alert('Unable to load profile');
    }
  };

  const handleProfileClick = async () => {
    if (!showProfile && !user) {
      await fetchUserProfile();
    }
    setShowProfile(prev => !prev);
    setEditMode(false);
  };

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = async () => {
    try {
      await axios.put(
        'http://localhost:5144/User/Update',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );
      alert('Profile updated successfully');
      setEditMode(false);
      await fetchUserProfile();
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Failed to update profile');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between mb-3">
        <button className="btn btn-primary" onClick={handleProfileClick}>
          {showProfile ? 'Close Profile' : 'My Profile'}
        </button>
        <a className="btn btn-success" href="/Home">Home</a>
        <a className="btn btn-danger" href="/Login">Logout</a>
      </div>

      {showProfile && user && (
        <div className="card p-4">
          <h3>My Profile</h3>
          <div className="form-group mb-2">
            <label>Email:</label>
            <input
              type="email"
              className="form-control"
              name="email"
              value={formData.email}
              onChange={handleChange}
              disabled={!editMode}
            />
          </div>
          <div className="form-group mb-2">
            <label>Contact Number:</label>
            <input
              type="text"
              className="form-control"
              name="contactNumber"
              value={formData.contactNumber}
              onChange={handleChange}
              disabled={!editMode}
            />
          </div>
          <div className="form-group mb-2">
            <label>Password:</label>
            <input
              type="password"
              className="form-control"
              name="password"
              value={formData.password}
              onChange={handleChange}
              disabled={!editMode}
            />
          </div>

          {!editMode ? (
            <button className="btn btn-warning mt-2" onClick={handleEditClick}>
              Edit
            </button>
          ) : (
            <button className="btn btn-success mt-2" onClick={handleSaveClick}>
              Save
            </button>
          )}
        </div>
      )}

      <Categories />
    </div>
  );
}

export default Welcome;
