import React, { useState, useEffect } from "react";
import axios from "axios";

const API_BASE = "https://localhost:5001"; // Replace with your actual backend URL

const getToken = () => localStorage.getItem("token");

const getConfig = () => ({
  headers: { Authorization: `Bearer ${getToken()}` },
});

function AdminPanel() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState("");

  const [currentAdmin, setCurrentAdmin] = useState(null);
  const [admins, setAdmins] = useState([]);
  const [message, setMessage] = useState("");
  const [editAdminId, setEditAdminId] = useState(null);
  const [loading, setLoading] = useState(false);

  const [formAdmin, setFormAdmin] = useState({
    AdminId: 0,
    Name: "",
    Role: "",
    Permissions: "",
    Email: "",
    Password: "",
  });

  useEffect(() => {
    const token = getToken();
    if (token) {
      fetchProfile();
      fetchAdmins();
    }
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError("");
    try {
      const loginUrl = `${API_BASE}/Admin/Login`;
      const res = await axios.post(loginUrl, { email, password });
      const token = res.data.token;
      if (!token) {
        setLoginError("Login failed: No token received.");
        return;
      }
      localStorage.setItem("token", token);
      localStorage.setItem("email", email);
      setEmail("");
      setPassword("");
      fetchProfile();
      fetchAdmins();
    } catch (err) {
      console.error("Login error:", err);
      setLoginError("Invalid email or password");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    setCurrentAdmin(null);
    setAdmins([]);
    setEditAdminId(null);
    setMessage("");
  };

  const fetchProfile = async () => {
    setLoading(true);
    try {
      const emailStored = localStorage.getItem("email");
      const profileUrl = `${API_BASE}/Admin/Profile?email=${emailStored}`;
      const res = await axios.get(profileUrl, getConfig());
      setCurrentAdmin(res.data);
    } catch (error) {
      console.error("Fetch profile error:", error);
      handleLogout();
    } finally {
      setLoading(false);
    }
  };

  const fetchAdmins = async () => {
    try {
      const listUrl = `${API_BASE}/Admin/ListofAdmins`;
      const res = await axios.get(listUrl, getConfig());
      setAdmins(res.data);
    } catch (err) {
      console.error("Failed to fetch admins", err);
    }
  };

  const startEdit = (admin) => {
    setFormAdmin({
      AdminId: admin.AdminId,
      Name: admin.Name,
      Role: admin.Role,
      Permissions: admin.Permissions,
      Email: admin.Email,
      Password: "",
    });
    setEditAdminId(admin.AdminId);
    setMessage("");
  };

  const resetForm = () => {
    setFormAdmin({
      AdminId: 0,
      Name: "",
      Role: "",
      Permissions: "",
      Email: "",
      Password: "",
    });
    setEditAdminId(null);
    setMessage("");
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormAdmin((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmitForm = async (e) => {
    e.preventDefault();

    if (!formAdmin.Name || !formAdmin.Role || !formAdmin.Email) {
      setMessage("Please fill all required fields: Name, Role, Email.");
      return;
    }

    try {
      if (editAdminId) {
        const updateUrl = `${API_BASE}/Admin/Update?id=${formAdmin.AdminId}`;
        await axios.put(updateUrl, formAdmin, getConfig());
        setMessage("Admin updated successfully.");
      } else {
        const createUrl = `${API_BASE}/Admin/CreateAdmin`;
        await axios.post(createUrl, formAdmin, getConfig());
        setMessage("Admin created successfully.");
      }
      resetForm();
      fetchAdmins();
    } catch (err) {
      console.error("Operation failed:", err.response?.data || err.message);
      setMessage("Operation failed. Check console for details.");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this admin?")) return;

    try {
      const deleteUrl = `${API_BASE}/Admin/Delete?id=${id}`;
      await axios.delete(deleteUrl, getConfig());
      setMessage("Admin deleted.");
      fetchAdmins();
      if (editAdminId === id) resetForm();
    } catch (err) {
      console.error("Delete failed:", err);
      setMessage("Delete failed.");
    }
  };

  const styles = {
    container: { maxWidth: 900, margin: "auto", padding: 20, fontFamily: "Arial" },
    input: { width: "100%", padding: 8, marginBottom: 10 },
    button: { padding: 10, marginRight: 10 },
    table: { width: "100%", borderCollapse: "collapse", marginTop: 20 },
    th: { backgroundColor: "#f0f0f0", padding: 8 },
    td: { padding: 8, border: "1px solid #ccc" },
    loginBox: { maxWidth: 400, margin: "auto", padding: 20 },
    error: { color: "red" },
  };

  if (!getToken()) {
    return (
      <div style={styles.loginBox}>
        <h2>Admin Login</h2>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            required
            onChange={(e) => setEmail(e.target.value)}
            style={styles.input}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            required
            onChange={(e) => setPassword(e.target.value)}
            style={styles.input}
          />
          <button type="submit" style={styles.button}>
            Login
          </button>
        </form>
        {loginError && <p style={styles.error}>{loginError}</p>}
      </div>
    );
  }

  if (loading) return <p>Loading profile...</p>;

  return (
    <div style={styles.container}>
      <h2>
        Welcome, {currentAdmin?.Name} (Role: {currentAdmin?.Role})
      </h2>
      <button onClick={handleLogout} style={{ ...styles.button, marginBottom: 20 }}>
        Logout
      </button>

      <h3>Manage Admins</h3>

      <form onSubmit={handleSubmitForm} style={{ marginBottom: 20 }}>
        <input
          type="text"
          name="Name"
          placeholder="Name *"
          value={formAdmin.Name}
          onChange={handleFormChange}
          required
          style={styles.input}
        />
        <input
          type="email"
          name="Email"
          placeholder="Email *"
          value={formAdmin.Email}
          onChange={handleFormChange}
          required
          style={styles.input}
        />
        <input
          type="text"
          name="Role"
          placeholder="Role *"
          value={formAdmin.Role}
          onChange={handleFormChange}
          required
          style={styles.input}
        />
        <input
          type="text"
          name="Permissions"
          placeholder="Permissions"
          value={formAdmin.Permissions}
          onChange={handleFormChange}
          style={styles.input}
        />
        <input
          type="password"
          name="Password"
          placeholder={editAdminId ? "New Password (optional)" : "Password *"}
          value={formAdmin.Password}
          onChange={handleFormChange}
          required={!editAdminId}
          style={styles.input}
        />

        <button type="submit" style={styles.button}>
          {editAdminId ? "Update Admin" : "Add Admin"}
        </button>
        {editAdminId && (
          <button type="button" onClick={resetForm} style={styles.button}>
            Cancel
          </button>
        )}
      </form>

      {message && <p>{message}</p>}

      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.th}>Name</th>
            <th style={styles.th}>Email</th>
            <th style={styles.th}>Role</th>
            <th style={styles.th}>Permissions</th>
            <th style={styles.th}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {admins.length === 0 && (
            <tr>
              <td colSpan="5" style={{ textAlign: "center" }}>
                No admins found.
              </td>
            </tr>
          )}
          {admins.map((admin) => (
            <tr key={admin.AdminId}>
              <td style={styles.td}>{admin.Name}</td>
              <td style={styles.td}>{admin.Email}</td>
              <td style={styles.td}>{admin.Role}</td>
              <td style={styles.td}>{admin.Permissions}</td>
              <td style={styles.td}>
                <button onClick={() => startEdit(admin)} style={styles.button}>
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(admin.AdminId)}
                  style={{ ...styles.button, color: "red" }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPanel;
