import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    contactNumber: ''
  });

  const token = localStorage.getItem('token');

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get('http://localhost:5144/User/Profile', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      const userData = response.data;
      setUser(userData);
      setFormData({
        email: userData.email || '',
        password: '',
        contactNumber: userData.contactNumber || ''
      });
    } catch (error) {
      console.error('Error fetching user profile:', error);
      alert('Unable to load profile');
    }
  };

  const handleProfileClick = async () => {
    if (!showProfile && !user) {
      await fetchUserProfile();
    }
    setShowProfile(prev => !prev);
    setEditMode(false);
  };

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    try {
      await axios.put('http://localhost:5144/User/UpdateProfile', formData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setUser(prev => ({ ...prev, ...formData }));
      setEditMode(false);
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Failed to update profile');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-end mb-3">
        <button className="btn btn-info me-2" onClick={handleProfileClick}>
          My Profile
        </button>
        <button className="btn btn-danger" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {showProfile ? (
        <div className="card p-4">
          <h2>User Profile</h2>
          <div className="mb-3">
            <label>Email:</label>
            {editMode ? (
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                className="form-control"
              />
            ) : (
              <p>{user?.email}</p>
            )}
          </div>
          <div className="mb-3">
            <label>Contact Number:</label>
            {editMode ? (
              <input
                type="text"
                name="contactNumber"
                value={formData.contactNumber}
                onChange={handleInputChange}
                className="form-control"
              />
            ) : (
              <p>{user?.contactNumber}</p>
            )}
          </div>
          {editMode ? (
            <>
              <button className="btn btn-success me-2" onClick={handleSave}>Save</button>
              <button className="btn btn-secondary" onClick={() => setEditMode(false)}>Cancel</button>
            </>
          ) : (
            <button className="btn btn-warning" onClick={() => setEditMode(true)}>Edit</button>
          )}
        </div>
      ) : (
        <Categories />
      )}
    </div>
  );
}

export default Welcome;
