import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import ProductCard from './ProductCard';
import Profile from './Profile';
import './Welcome.css';

function Welcome({ logout }) {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [showProfile, setShowProfile] = useState(false);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const email = localStorage.getItem('email');

    if (!token || !email) {
      navigate('/login');
      return;
    }

    // Load cart immediately based on email (temporary key)
    const tempCart = localStorage.getItem(`cartItems_${email}`);
    if (tempCart) {
      setCartItems(JSON.parse(tempCart));
    }

    try {
      const tokenData = JSON.parse(atob(token.split('.')[1]));
      const role = tokenData.role || 'User';

      const endpoint =
        role === 'Admin'
          ? `http://localhost:5144/Admin/Profile?email=${email}`
          : `http://localhost:5144/User/Profile?email=${email}`;

      axios
        .get(endpoint, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setUser(response.data);
          setUpdatedUser(response.data);

          // Once we have userId, check if a cart exists for that user
          const savedCart = localStorage.getItem(`cartItems_${response.data.userId}`);
          if (savedCart) {
            setCartItems(JSON.parse(savedCart));
          } else {
            // Move temp cart to userId key
            if (tempCart) {
              localStorage.setItem(`cartItems_${response.data.userId}`, tempCart);
            }
          }
        })
        .catch((err) => {
          console.error('Error fetching profile on load:', err);
          navigate('/login');
        });
    } catch (err) {
      console.error('Error parsing token on load:', err);
      logout();
    }
  }, [navigate, logout]);

  // Save cart to localStorage for this specific user
  useEffect(() => {
    if (user && user.userId) {
      localStorage.setItem(`cartItems_${user.userId}`, JSON.stringify(cartItems));
    }
  }, [cartItems, user]);

  const addToCart = (product) => {
    setCartItems((prevItems) => {
      const existingItem = prevItems.find((item) => item.productId === product.productId);
      if (existingItem) {
        return prevItems.map((item) =>
          item.productId === product.productId
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prevItems, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (productId) => {
    setCartItems((prevItems) =>
      prevItems
        .map((item) =>
          item.productId === productId
            ? { ...item, quantity: item.quantity - 1 }
            : item
        )
        .filter((item) => item.quantity > 0)
    );
  };

  const handleLogout = () => {
    setUser(null);
    setShowProfile(false);
    setShowCart(false);
    logout();
  };

  return (
    <div className="welcome-container">
      {user && (
        <div className="header">
          <h2>
            Welcome, {user.firstName} {user.lastName}!
          </h2>
          <button onClick={handleLogout}>Logout</button>
          <button onClick={() => setShowProfile(!showProfile)}>Profile</button>
          <button onClick={() => setShowCart(!showCart)}>
            Cart ({cartItems.reduce((total, item) => total + item.quantity, 0)})
          </button>
        </div>
      )}

      {showProfile && <Profile user={updatedUser} />}

      {showCart && (
        <div className="cart">
          <h3>Your Cart</h3>
          {cartItems.length === 0 ? (
            <p>Cart is empty</p>
          ) : (
            <ul>
              {cartItems.map((item) => (
                <li key={item.productId}>
                  {item.name} - ${item.price} × {item.quantity}
                  <button onClick={() => removeFromCart(item.productId)}>Remove</button>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}

      <div className="products">
        {/* Example static products — replace with your product API */}
        {[
          { productId: 1, name: 'Product A', price: 10 },
          { productId: 2, name: 'Product B', price: 20 },
        ].map((product) => (
          <ProductCard
            key={product.productId}
            product={product}
            addToCart={addToCart}
          />
        ))}
      </div>
    </div>
  );
}

export default Welcome;
