import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    phone: '',
    role: '',
  });

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('https://localhost:7155/User/profile', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setUser(response.data);
        setFormData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching user profile:', error);
      });
    }
  }, []);

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = () => {
    const token = localStorage.getItem('token');
    axios.put('https://localhost:7155/User/profile', formData, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
    .then((response) => {
      setUser(response.data);
      setEditMode(false);
      alert('Profile updated successfully!');
    })
    .catch((error) => {
      console.error('Error updating profile:', error);
      alert('Failed to update profile.');
    });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  const handleHome = () => {
    setShowProfile(false); // Go back to home
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-end mb-3">
        <button className="btn btn-info me-2" onClick={() => setShowProfile(true)}>My Profile</button>
        <button className="btn btn-secondary me-2" onClick={handleHome}>Home</button>
        <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
      </div>

      {showProfile ? (
        <div className="card p-4 shadow">
          <h3 className="mb-4">User Profile</h3>
          <form>
            <div className="mb-3">
              <label>Name:</label>
              <input
                type="text"
                className="form-control"
                name="name"
                value={formData.name}
                onChange={handleChange}
                disabled={!editMode}
              />
            </div>
            <div className="mb-3">
              <label>Email:</label>
              <input
                type="email"
                className="form-control"
                name="email"
                value={formData.email}
                onChange={handleChange}
                disabled={!editMode}
              />
            </div>
            <div className="mb-3">
              <label>Username:</label>
              <input
                type="text"
                className="form-control"
                name="username"
                value={formData.username}
                onChange={handleChange}
                disabled={!editMode}
              />
            </div>
            <div className="mb-3">
              <label>Phone:</label>
              <input
                type="text"
                className="form-control"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                disabled={!editMode}
              />
            </div>
            <div className="mb-3">
              <label>Role:</label>
              <input
                type="text"
                className="form-control"
                name="role"
                value={formData.role}
                disabled
              />
            </div>

            {editMode ? (
              <button type="button" className="btn btn-success" onClick={handleSaveClick}>
                Save
              </button>
            ) : (
              <button type="button" className="btn btn-primary" onClick={handleEditClick}>
                Edit
              </button>
            )}
          </form>
        </div>
      ) : (
        <Categories />
      )}
    </div>
  );
}

export default Welcome;
