// src/components/ProductList.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductService from '../services/ProductService';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    description: '',
    price: '',
    category: '',
    imageURL: '',
  });

  // Fetch all products on mount
  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, []);

  // Fetch products
  const fetchProducts = () => {
    ProductService.getProducts()
      .then((res) => setProducts(res.data))
      .catch((err) => console.error('Error fetching products:', err));
  };

  // Fetch categories (for dropdown)
  const fetchCategories = () => {
    const token = localStorage.getItem('token');
    fetch('http://localhost:5144/Category', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setCategories(data))
      .catch((err) => console.error('Error fetching categories:', err));
  };

  // Fetch products by category
  const fetchProductsByCategory = (category) => {
    ProductService.getProductsByCategory(category)
      .then((res) => setProducts(res.data))
      .catch((err) => console.error('Error fetching category products:', err));
  };

  // Handle deleting a product
  const handleDelete = (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    ProductService.deleteProduct(id)
      .then(() => fetchProducts())
      .catch((err) => console.error('Error deleting product:', err));
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prev) => ({ ...prev, [name]: value }));
  };

  // Handle adding a new product
  const handleAddProduct = (e) => {
    e.preventDefault();

    if (!newProduct.productName || !newProduct.price || !newProduct.category) {
      alert('Please fill in product name, price, and category');
      return;
    }

    const productToSend = {
      ...newProduct,
      productId: 0,
      price: Number(newProduct.price),
    };

    ProductService.addProduct(productToSend)
      .then(() => {
        alert('Product added successfully!');
        setNewProduct({
          productName: '',
          description: '',
          price: '',
          category: '',
          imageURL: '',
        });
        fetchProducts();
      })
      .catch((err) => {
        console.error('Error adding product:', err);
        alert('Failed to add product');
      });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Product List</h2>

      {/* Add Product Form */}
      <form onSubmit={handleAddProduct} style={{ marginBottom: '30px' }}>
        <input
          type="text"
          name="productName"
          placeholder="Product Name"
          value={newProduct.productName}
          onChange={handleInputChange}
          required
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newProduct.description}
          onChange={handleInputChange}
          style={{ marginRight: '10px' }}
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={newProduct.price}
          onChange={handleInputChange}
          required
          style={{ marginRight: '10px' }}
        />
        <select
          name="category"
          value={newProduct.category}
          onChange={handleInputChange}
          required
          style={{ marginRight: '10px' }}
        >
          <option value="">Select category</option>
          {categories.map((cat) => (
            <option key={cat.categoryId} value={cat.name}>
              {cat.name}
            </option>
          ))}
        </select>
        <input
          type="text"
          name="imageURL"
          placeholder="Image URL"
          value={newProduct.imageURL}
          onChange={handleInputChange}
          style={{ marginRight: '10px' }}
        />
        <button type="submit">Add Product</button>
      </form>

      {/* Category Filter Buttons */}
      <div style={{ marginBottom: '20px' }}>
        {categories.map((cat) => (
          <button
            key={cat.categoryId}
            style={{ marginRight: '10px' }}
            onClick={() => fetchProductsByCategory(cat.name)}
          >
            {cat.name}
          </button>
        ))}
        <button onClick={fetchProducts}>All</button>
      </div>

      {/* Products List */}
      <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
        {products.map((product) => (
          <li
            key={product.productId}
            style={{
              marginBottom: '15px',
              padding: '10px',
              border: '1px solid #ccc',
              borderRadius: '6px',
            }}
          >
            <strong>{product.productName}</strong> - Rs {product.price} <br />
            <em>{product.description}</em> <br />
            <small>Category: {product.category}</small> <br />
            {product.imageURL && (
              <img
                src={product.imageURL}
                alt={product.productName}
                height={80}
                style={{ marginTop: '10px' }}
              />
            )}
            <div style={{ marginTop: '10px' }}>
              <Link
                to={`/edit/${product.productId}`}
                style={{
                  marginRight: '10px',
                  padding: '5px 10px',
                  backgroundColor: 'blue',
                  color: 'white',
                  textDecoration: 'none',
                  borderRadius: '4px',
                }}
              >
                Edit
              </Link>
              <button
                onClick={() => handleDelete(product.productId)}
                style={{
                  padding: '5px 10px',
                  backgroundColor: 'red',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                }}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;
