import React, { useState, useEffect } from 'react';
import ProductService from '../services/ProductService';

function ProductList() {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [products, setProducts] = useState([]);

  const [newProduct, setNewProduct] = useState({
    productName: '',
    description: '',
    price: '',
    category: '',
    imageURL: '',
  });

  // Fetch all products
  const fetchProducts = () => {
    ProductService.getProducts()
      .then((res) => setProducts(res.data))
      .catch((err) => console.error('Error fetching products', err));
  };

  // Fetch categories
  const fetchCategories = () => {
    const token = localStorage.getItem('token');
    fetch('http://localhost:5144/Category', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setCategories(data))
      .catch((err) => console.error('Error fetching categories', err));
  };

  // Fetch products by category
  const fetchProductsByCategory = (categoryName) => {
    ProductService.getProductsByCategory(categoryName)
      .then((res) => {
        setProducts(res.data);
        setSelectedCategory(categoryName);
      })
      .catch((err) => {
        console.error('Error fetching category products', err);
        setProducts([]);
      });
  };

  // Add new product handler
  const handleAddProduct = (e) => {
    e.preventDefault();

    if (!newProduct.productName || !newProduct.price || !newProduct.category) {
      alert('Please fill product name, price, and category');
      return;
    }

    ProductService.addProduct(newProduct)
      .then(() => {
        alert('Product added!');
        setNewProduct({ productName: '', description: '', price: '', category: '', imageURL: '' });
        fetchProducts();
      })
      .catch((err) => {
        console.error('Error adding product:', err);
        alert('Failed to add product');
      });
  };

  // Delete product handler
  const handleDelete = (id) => {
    ProductService.deleteProduct(id)
      .then(() => {
        alert('Product deleted!');
        fetchProducts();
      })
      .catch((err) => console.error('Error deleting product:', err));
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts();
  }, []);

  return (
    <div className="container">
      <h2>Product Management</h2>

      {/* Add Product Form */}
      <form onSubmit={handleAddProduct} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          name="productName"
          placeholder="Product Name"
          value={newProduct.productName}
          onChange={(e) => setNewProduct({ ...newProduct, productName: e.target.value })}
          required
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newProduct.description}
          onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
          required
        />
        <select
          name="category"
          value={newProduct.category}
          onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })}
          required
        >
          <option value="">Select category</option>
          {categories.map((cat) => (
            <option key={cat.categoryId} value={cat.name}>
              {cat.name}
            </option>
          ))}
        </select>
        <input
          type="text"
          name="imageURL"
          placeholder="Image URL"
          value={newProduct.imageURL}
          onChange={(e) => setNewProduct({ ...newProduct, imageURL: e.target.value })}
        />
        <button type="submit" style={{ marginLeft: '10px' }}>
          Add Product
        </button>
      </form>

      {/* Category Filter Buttons */}
      <div style={{ marginBottom: '20px' }}>
        <button onClick={() => fetchProducts()}>All</button>
        {categories.map((cat) => (
          <button key={cat.categoryId} onClick={() => fetchProductsByCategory(cat.name)} style={{ marginLeft: '10px' }}>
            {cat.name}
          </button>
        ))}
      </div>

      {/* Product List */}
      <ul>
        {products.map((product) => (
          <li key={product.productId} style={{ marginBottom: '15px' }}>
            <strong>{product.productName}</strong> - Rs {product.price} - {product.category}
            <br />
            <img
              src={product.imageURL || 'https://via.placeholder.com/100x50?text=No+Image'}
              alt={product.productName}
              style={{ height: '50px', width: '100px', objectFit: 'cover', marginTop: '5px' }}
            />
            <br />
            <button onClick={() => handleDelete(product.productId)} style={{ color: 'red', marginTop: '5px' }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;
