import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [loading, setLoading] = useState(false); // add loading flag

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    contactNumber: '',
    shippingAddress: '',
    paymentDetails: '',
    permissions: '',
  });

  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');
  const email = localStorage.getItem('email');

  const fetchProfile = async () => {
    setLoading(true);
    try {
      const url =
        role === 'Admin'
          ? `http://localhost:5144/Admin/Profile?email=${email}`
          : `http://localhost:5144/User/Profile?email=${email}`;

      const { data } = await axios.get(url, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setUser(data);

      setFormData({
        name: data.name || '',
        email: data.email || '',
        password: '',
        contactNumber: data.phone || '',
        shippingAddress: data.shippingAddress || '',
        paymentDetails: data.paymentDetails || '',
        permissions: data.permissions || '',
      });
    } catch (e) {
      console.error('Profile fetch failed:', e);
      setUser(null);
    }
    setLoading(false);
  };

  // Fetch profile once on load
  useEffect(() => {
    if (token && email && role) {
      fetchProfile();
    }
  }, [token, email, role]);

  const handleInputChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleProfileClick = () => {
    setShowProfile(!showProfile);
    setEditMode(false);
  };

  const handleUpdate = async () => {
    try {
      const url =
        role === 'Admin'
          ? `http://localhost:5144/Admin/Update?id=${user.adminId}`
          : `http://localhost:5144/User/Update?id=${user.userId}`;

      const updateData = { ...user, ...formData };

      await axios.put(url, updateData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert('Profile updated');
      setEditMode(false);
      fetchProfile();
    } catch (e) {
      console.error('Update failed:', e);
      alert('Update failed');
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete your account?')) return;

    try {
      const url =
        role === 'Admin'
          ? `http://localhost:5144/Admin/Delete?id=${user.adminId}`
          : `http://localhost:5144/User/Delete?id=${user.userId}`;

      await axios.delete(url, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert('Account deleted');
      localStorage.clear();
      window.location.href = '/login';
    } catch (e) {
      console.error('Delete failed:', e);
      alert('Delete failed');
    }
  };

  if (!token)
    return (
      <div className="text-center mt-5">
        <h4>Please login to view your profile.</h4>
      </div>
    );

  return (
    <div className="container-fluid p-0" style={{ background: '#f0f8ff', minHeight: '100vh' }}>
      {/* Navbar */}
      <nav className="navbar navbar-light bg-white shadow-sm px-4 mb-3">
        <div className="container-fluid justify-content-end">
          <div className="text-end">
            <div className="fw-bold mb-2 text-primary fs-5">
              {user ? `Welcome, ${user.name}` : 'Welcome!'}
            </div>
            <div className="d-flex gap-2">
              <button className="btn btn-primary" onClick={() => window.location.href = '/welcome'}>
                Home
              </button>
              <button className="btn btn-secondary" onClick={handleProfileClick}>
                My Profile
              </button>
              <button
                className="btn btn-danger"
                onClick={() => {
                  localStorage.clear();
                  window.location.href = '/login';
                }}
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="container">
        <h2 className="text-center mb-1">Welcome to Shop for Life</h2>
        <p className="text-center text-muted mb-4" style={{ fontStyle: 'italic' }}>
          "Shopping is the best entertainment to entertain me."
        </p>

        {showProfile && user ? (
          <div className="card shadow-sm mb-4">
            <div className="card-body">
              {loading ? (
                <p>Loading profile...</p>
              ) : (
                <>
                  <div className="d-flex align-items-center mb-3">
                    <img
                      src={`https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=random&rounded=true&size=80`}
                      alt="Avatar"
                      className="me-3 rounded-circle"
                    />
                    <h4 className="mb-0">{user.name}</h4>
                  </div>

                  {!editMode ? (
                    <>
                      <p><strong>Email:</strong> {user.email}</p>
                      {role === 'User' && (
                        <>
                          <p><strong>Phone:</strong> {user.phone}</p>
                          <p><strong>Shipping Address:</strong> {user.shippingAddress}</p>
                          <p><strong>Payment Details:</strong> {user.paymentDetails}</p>
                        </>
                      )}
                      {role === 'Admin' && (
                        <>
                          <p><strong>Role:</strong> {user.role}</p>
                          <p><strong>Permissions:</strong> {user.permissions}</p>
                        </>
                      )}
                      <button className="btn btn-warning me-2" onClick={() => setEditMode(true)}>
                        Edit Profile
                      </button>
                      <button className="btn btn-outline-danger" onClick={handleDelete}>
                        Delete Account
                      </button>
                    </>
                  ) : (
                    <>
                      <div className="mb-3">
                        <label>Name:</label>
                        <input type="text" className="form-control" name="name" value={formData.name} onChange={handleInputChange} />
                      </div>
                      <div className="mb-3">
                        <label>Email:</label>
                        <input type="email" className="form-control" name="email" value={formData.email} onChange={handleInputChange} />
                      </div>
                      <div className="mb-3">
                        <label>Password:</label>
                        <input type="password" className="form-control" name="password" value={formData.password} onChange={handleInputChange} />
                      </div>
                      {role === 'User' && (
                        <>
                          <div className="mb-3">
                            <label>Contact Number:</label>
                            <input type="text" className="form-control" name="contactNumber" value={formData.contactNumber} onChange={handleInputChange} />
                          </div>
                          <div className="mb-3">
                            <label>Shipping Address:</label>
                            <input type="text" className="form-control" name="shippingAddress" value={formData.shippingAddress} onChange={handleInputChange} />
                          </div>
                          <div className="mb-3">
                            <label>Payment Details:</label>
                            <input type="text" className="form-control" name="paymentDetails" value={formData.paymentDetails} onChange={handleInputChange} />
                          </div>
                        </>
                      )}
                      {role === 'Admin' && (
                        <div className="mb-3">
                          <label>Permissions:</label>
                          <input type="text" className="form-control" name="permissions" value={formData.permissions} onChange={handleInputChange} />
                        </div>
                      )}
                      <button className="btn btn-success me-2" onClick={handleUpdate}>Save</button>
                      <button className="btn btn-secondary" onClick={() => setEditMode(false)}>Cancel</button>
                    </>
                  )}
                </>
              )}
            </div>
          </div>
        ) : (
          <>
            <h3 className="text-center mt-5 mb-4">Explore More Categories</h3>
            <div className="container-fluid d-flex justify-content-center gap-4 flex-wrap">
              <Categories />
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Welcome;
