import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

import Login from './components/Login';
import Register from './components/Register';
import AuthService from './services/AuthService';
import Welcome from './components/Welcome';
import CategoryService from './services/CategoryService';
import HeaderNav from './components/HeaderNav';

function App() {
  const [user, setUser] = useState(null);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const token = AuthService.getToken();
    if (token) {
      const user = AuthService.getUserFromToken(token);
      setUser(user);
    }
  }, []);

  useEffect(() => {
    if (user) {
      fetchCategories();
    }
  }, [user]);

  const fetchCategories = async () => {
    try {
      const data = await CategoryService.getAllCategories();

      // ✅ Filter only categories that contain items
      const nonEmptyCategories = data.filter(cat => cat.items && cat.items.length > 0);

      setCategories(nonEmptyCategories);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const login = async (email, password) => {
    try {
      const token = await AuthService.login(email, password);
      const user = AuthService.getUserFromToken(token);
      setUser(user);
    } catch (error) {
      console.error('Error logging in', error);
    }
  };

  const logout = () => {
    AuthService.logout();
    setUser(null);
    setCategories([]);
  };

  return (
    <Router>
      <HeaderNav user={user} logout={logout} />

      {/* Show only non-empty categories */}
      {user && categories.length > 0 && (
        <div className="container mt-4">
          <h4 className="mb-3">Available Categories</h4>
          <div className="d-flex flex-wrap gap-4">
            {categories.map((cat, index) => (
              <div
                key={index}
                className="border rounded p-4 shadow-sm bg-light"
                style={{ minWidth: '150px', textAlign: 'center' }}
              >
                {cat.name}
              </div>
            ))}
          </div>
        </div>
      )}

      <Routes>
        <Route path="/" element={<Navigate to="/login" />} />
        <Route path="/Welcome" element={user ? <Welcome /> : <Navigate to="/login" />} />
        <Route path="/login" element={!user ? <Login login={login} /> : <Navigate to="/Welcome" />} />
        <Route path="/register" element={<Register />} />
        <Route path="/services/CategoryService" element={user ? <CategoryService /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
