// components/HeaderNav.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function HeaderNav({ user, logout }) {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get("http://localhost:5144/Category", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCategories(response.data);
      } catch (error) {
        console.error("Failed to fetch categories:", error);
      }
    };

    if (user) {
      fetchCategories();
    }
  }, [user]);

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light px-4">
      <Link className="navbar-brand" to="/">My E-Commerce</Link>
      
      <div className="collapse navbar-collapse">
        <ul className="navbar-nav me-auto">
          {user && (
            <>
              <li className="nav-item">
                <Link className="nav-link" to="/Welcome">Home</Link>
              </li>

              {/* Dropdown or list of categories */}
              <li className="nav-item dropdown">
                <span className="nav-link dropdown-toggle" role="button" data-bs-toggle="dropdown">
                  Categories
                </span>
                <ul className="dropdown-menu">
                  {categories.length > 0 ? (
                    categories.map((cat) => (
                      <li key={cat.categoryId}>
                        <span className="dropdown-item">{cat.name}</span>
                      </li>
                    ))
                  ) : (
                    <li><span className="dropdown-item text-muted">No Categories</span></li>
                  )}
                </ul>
              </li>

              <li className="nav-item">
                <Link className="nav-link" to="/services/CategoryService">Manage Categories</Link>
              </li>
            </>
          )}
        </ul>

        <div className="d-flex">
          {user ? (
            <>
              <span className="navbar-text me-3">Welcome, {user.email}</span>
              <button className="btn btn-outline-danger" onClick={logout}>Logout</button>
            </>
          ) : (
            <span className="navbar-text text-danger">Not logged in yet!</span>
          )}
        </div>
      </div>
    </nav>
  );
}

export default HeaderNav;
