import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);

  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  const fetchUserProfile = async () => {
    try {
      const endpoint = role === 'Admin' ? '/Admin/Profile' : '/User/Profile';
      const response = await axios.get(endpoint, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(response.data);
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  const handleProfileClick = () => {
    setShowProfile(true);
    fetchUserProfile();
  };

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = async () => {
    try {
      const endpoint = role === 'Admin' ? '/Admin/Profile' : '/User/Profile';
      await axios.put(endpoint, user, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setEditMode(false);
    } catch (error) {
      console.error('Error saving profile:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    window.location.href = '/';
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((prevUser) => ({ ...prevUser, [name]: value }));
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Welcome to Our Shop</h2>
        <div>
          {!token ? (
            <a href="/login" className="btn btn-primary">Login</a>
          ) : (
            <>
              <button className="btn btn-secondary me-2" onClick={handleProfileClick}>My Profile</button>
              <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
            </>
          )}
        </div>
      </div>

      {showProfile && user && (
        <div className="card mb-4">
          <div className="card-body">
            <h5 className="card-title">My Profile</h5>
            <form>
              <div className="mb-3">
                <label className="form-label">Name:</label>
                <input
                  type="text"
                  name="name"
                  value={user.name}
                  onChange={handleChange}
                  className="form-control"
                  readOnly={!editMode}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Email:</label>
                <input
                  type="email"
                  name="email"
                  value={user.email}
                  className="form-control"
                  readOnly
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Phone:</label>
                <input
                  type="text"
                  name="phone"
                  value={user.phone}
                  onChange={handleChange}
                  className="form-control"
                  readOnly={!editMode}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Address:</label>
                <input
                  type="text"
                  name="address"
                  value={user.address}
                  onChange={handleChange}
                  className="form-control"
                  readOnly={!editMode}
                />
              </div>
              {!editMode ? (
                <button type="button" className="btn btn-warning" onClick={handleEditClick}>
                  Edit
                </button>
              ) : (
                <button type="button" className="btn btn-success" onClick={handleSaveClick}>
                  Save
                </button>
              )}
            </form>
          </div>
        </div>
      )}

      <Categories />
    </div>
  );
}

export default Welcome;
