import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '' });
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const headers = { Authorization: `Bearer ${token}` };

        // Try User endpoint
        let res = await axios.get('https://localhost:7282/User/UserProfile', { headers });
        if (res.data) {
          setUser(res.data);
          setFormData({ name: res.data.name, email: res.data.email });
          setIsAdmin(false);
          return;
        }

      } catch (userErr) {
        try {
          // Try Admin endpoint
          const res = await axios.get('http://localhost:5144/Admin/Profile', {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          });
          if (res.data) {
            setUser(res.data);
            setFormData({ name: res.data.name, email: res.data.email });
            setIsAdmin(true);
          }
        } catch (adminErr) {
          console.error('Error fetching profile from both endpoints');
        }
      }
    };

    fetchData();
  }, []);

  const handleEdit = () => setEditMode(true);
  const handleSave = () => {
    setUser({ ...user, ...formData });
    setEditMode(false);
  };

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const logout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  const goHome = () => {
    setShowProfile(false);
    setEditMode(false);
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between mb-3">
        <div>
          <button className="btn btn-primary me-2" onClick={() => setShowProfile(true)}>My Profile</button>
          <button className="btn btn-secondary" onClick={goHome}>Home</button>
        </div>
        <button className="btn btn-danger" onClick={logout}>Logout</button>
      </div>

      {showProfile ? (
        <div className="card p-4">
          <h3 className="mb-3">Welcome, {formData.name}</h3>
          {editMode ? (
            <div>
              <div className="mb-3">
                <label>Name</label>
                <input name="name" className="form-control" value={formData.name} onChange={handleChange} />
              </div>
              <div className="mb-3">
                <label>Email</label>
                <input name="email" className="form-control" value={formData.email} onChange={handleChange} />
              </div>
              <button className="btn btn-success" onClick={handleSave}>Save</button>
            </div>
          ) : (
            <div>
              <p><strong>Name:</strong> {formData.name}</p>
              <p><strong>Email:</strong> {formData.email}</p>
              <button className="btn btn-info" onClick={handleEdit}>Edit</button>
            </div>
          )}
        </div>
      ) : (
        <div>
          <h2>Welcome to Shopping</h2>
          <Categories />
        </div>
      )}
    </div>
  );
}

export default Welcome;
