import React, { useState, useEffect } from "react";
import axios from "axios";

const API_BASE = "http://localhost:5144";

const getToken = () => localStorage.getItem("token");

const getConfig = () => ({
  headers: { Authorization: `Bearer ${getToken()}` },
});

function AdminPanel() {
  // Login form state
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState("");

  // Admin info + list + editing
  const [currentAdmin, setCurrentAdmin] = useState(null);
  const [admins, setAdmins] = useState([]);
  const [message, setMessage] = useState("");
  const [editAdmin, setEditAdmin] = useState(null);
  const [loading, setLoading] = useState(false);

  // On component mount, load profile if logged in
  useEffect(() => {
    const token = getToken();
    if (token) {
      fetchProfile();
      fetchAdmins();
    }
  }, []);

  // Login handler
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError("");
    try {
      const res = await axios.post(`${API_BASE}/Admin/Login`, { email, password });
      const token = res.data.token;
      localStorage.setItem("token", token);
      localStorage.setItem("email", email);
      setEmail("");
      setPassword("");
      fetchProfile();
      fetchAdmins();
    } catch {
      setLoginError("Invalid email or password");
    }
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    setCurrentAdmin(null);
    setAdmins([]);
    setEditAdmin(null);
    setMessage("");
  };

  // Fetch logged-in admin profile
  const fetchProfile = async () => {
    setLoading(true);
    try {
      const emailStored = localStorage.getItem("email");
      const res = await axios.get(`${API_BASE}/Admin/Profile?email=${emailStored}`, getConfig());
      setCurrentAdmin(res.data);
    } catch {
      handleLogout();
    } finally {
      setLoading(false);
    }
  };

  // Fetch all admins (only for Admin role)
  const fetchAdmins = async () => {
    try {
      const res = await axios.get(`${API_BASE}/Admin/List`, getConfig());
      setAdmins(res.data);
    } catch (err) {
      console.error("Failed to fetch admins", err);
    }
  };

  // Form for adding or editing admin
  const [formAdmin, setFormAdmin] = useState({
    AdminId: 0,
    Name: "",
    Role: "",
    Permissions: "",
    Email: "",
    Password: "",
  });

  // Fill form for editing
  const startEdit = (admin) => {
    setFormAdmin({
      AdminId: admin.AdminId,
      Name: admin.Name,
      Role: admin.Role,
      Permissions: admin.Permissions,
      Email: admin.Email,
      Password: "", // empty password, optional to change
    });
    setEditAdmin(admin.AdminId);
    setMessage("");
  };

  // Reset form to empty (add mode)
  const resetForm = () => {
    setFormAdmin({
      AdminId: 0,
      Name: "",
      Role: "",
      Permissions: "",
      Email: "",
      Password: "",
    });
    setEditAdmin(null);
    setMessage("");
  };

  // Handle input changes in form
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormAdmin((prev) => ({ ...prev, [name]: value }));
  };

  // Submit add or update
  const handleSubmitForm = async (e) => {
    e.preventDefault();
    if (!formAdmin.Name || !formAdmin.Role || !formAdmin.Email) {
      setMessage("Please fill all required fields (Name, Role, Email)");
      return;
    }

    try {
      if (editAdmin) {
        // Update
        await axios.put(`${API_BASE}/Admin/Update?id=${formAdmin.AdminId}`, formAdmin, getConfig());
        setMessage("Admin updated successfully.");
      } else {
        // Create
        await axios.post(`${API_BASE}/Admin/Create`, formAdmin, getConfig());
        setMessage("Admin created successfully.");
      }
      resetForm();
      fetchAdmins();
    } catch (err) {
      setMessage("Operation failed.");
      console.error(err);
    }
  };

  // Delete admin
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this admin?")) return;
    try {
      await axios.delete(`${API_BASE}/Admin/Delete?id=${id}`, getConfig());
      setMessage("Admin deleted.");
      fetchAdmins();
      if (editAdmin === id) resetForm();
    } catch (err) {
      setMessage("Delete failed.");
      console.error(err);
    }
  };

  if (!getToken()) {
    // Show login form if not logged in
    return (
      <div style={{ maxWidth: 400, margin: "auto", padding: 20 }}>
        <h2>Admin Login</h2>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            required
            onChange={(e) => setEmail(e.target.value)}
            style={{ width: "100%", padding: 8, marginBottom: 10 }}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            required
            onChange={(e) => setPassword(e.target.value)}
            style={{ width: "100%", padding: 8, marginBottom: 10 }}
          />
          <button type="submit" style={{ width: "100%", padding: 10 }}>
            Login
          </button>
        </form>
        {loginError && <p style={{ color: "red" }}>{loginError}</p>}
      </div>
    );
  }

  if (loading) return <p>Loading profile...</p>;

  // Logged in: show admin dashboard and admin management
  return (
    <div style={{ maxWidth: 900, margin: "auto", padding: 20 }}>
      <h2>Welcome, {currentAdmin?.Name} (Role: {currentAdmin?.Role})</h2>
      <button onClick={handleLogout} style={{ marginBottom: 20 }}>
        Logout
      </button>

      <h3>Manage Admins</h3>

      {/* Admin form */}
      <form onSubmit={handleSubmitForm} style={{ marginBottom: 20 }}>
        <input
          type="text"
          name="Name"
          placeholder="Name *"
          value={formAdmin.Name}
          onChange={handleFormChange}
          required
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <input
          type="email"
          name="Email"
          placeholder="Email *"
          value={formAdmin.Email}
          onChange={handleFormChange}
          required
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <input
          type="text"
          name="Role"
          placeholder="Role *"
          value={formAdmin.Role}
          onChange={handleFormChange}
          required
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <input
          type="text"
          name="Permissions"
          placeholder="Permissions"
          value={formAdmin.Permissions}
          onChange={handleFormChange}
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <input
          type="password"
          name="Password"
          placeholder={editAdmin ? "New Password (optional)" : "Password *"}
          value={formAdmin.Password}
          onChange={handleFormChange}
          required={!editAdmin}
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />

        <button type="submit" style={{ padding: 10, marginRight: 10 }}>
          {editAdmin ? "Update Admin" : "Add Admin"}
        </button>
        {editAdmin && (
          <button type="button" onClick={resetForm} style={{ padding: 10 }}>
            Cancel
          </button>
        )}
      </form>

      {/* Message */}
      {message && <p>{message}</p>}

      {/* Admins list */}
      <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Permissions</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {admins.length === 0 && (
            <tr>
              <td colSpan="5" style={{ textAlign: "center" }}>
                No admins found.
              </td>
            </tr>
          )}
          {admins.map((a) => (
            <tr key={a.AdminId}>
              <td>{a.Name}</td>
              <td>{a.Email}</td>
              <td>{a.Role}</td>
              <td>{a.Permissions}</td>
              <td>
                <button onClick={() => startEdit(a)} style={{ marginRight: 10 }}>
                  Edit
                </button>
                <button onClick={() => handleDelete(a.AdminId)} style={{ color: "red" }}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPanel;
