import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Welcome({ logout }) {
  const [showProfile, setShowProfile] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [user, setUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState(null);
  const [showCart, setShowCart] = useState(false); // üõí Cart toggle
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }
  }, [navigate]);

  const handleProfileClick = async () => {
    const token = localStorage.getItem('token');
    const email = localStorage.getItem('email');

    if (!token || !email) {
      alert('You are not logged in');
      return;
    }

    try {
      const tokenData = JSON.parse(atob(token.split('.')[1]));
      const role = tokenData.role || 'User';

      const endpoint =
        role === 'Admin'
          ? `http://localhost:5144/Admin/Profile?email=${email}`
          : `http://localhost:5144/User/Profile?email=${email}`;

      const response = await axios.get(endpoint, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setUser(response.data);
      setUpdatedUser(response.data);
      setShowProfile(true);
      setEditMode(false);
      setShowCart(false);
    } catch (error) {
      console.error('Error fetching profile:', error);
      alert('Failed to load profile. Please try again.');
    }
  };

  const handleUpdate = async () => {
    const token = localStorage.getItem('token');
    try {
      await axios.put(
        `http://localhost:5144/User/Update?id=${updatedUser.userId}`,
        updatedUser,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      setUser(updatedUser);
      setEditMode(false);
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Failed to update profile.');
    }
  };

  const handleDelete = async () => {
    const confirmDelete = window.confirm('Are you sure you want to delete your account?');
    if (!confirmDelete) return;

    const token = localStorage.getItem('token');
    try {
      await axios.delete(`http://localhost:5144/User/Delete?id=${user.userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      alert('Account deleted successfully.');
      logout();
    } catch (error) {
      console.error('Error deleting account:', error);
      alert('Failed to delete account.');
    }
  };

  const handleLogout = () => {
    logout();
  };

  const handleCartClick = () => {
    setShowCart(true);
    setShowProfile(false);
  };

  return (
    <div className="container mt-3">
      {/* üöÄ Header Bar */}
      <div className="d-flex justify-content-between align-items-center mb-4 b-">
        <h2>Welcome to the Dashboard</h2>
        <div>
          <button className="btn btn-outline-primary me-2" onClick={handleProfileClick}>
            My Profile
          </button>
          <button className="btn btn-outline-success me-2" onClick={handleCartClick}>
            Cart
          </button>
          <button className="btn btn-outline-danger" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      {/* üë§ Profile Section */}
      {showProfile && user && (
        <div className="card p-3 shadow mb-4">
          <h3>My Profile</h3>

          {!editMode ? (
            <>
              <p><strong>Name:</strong> {user.name}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Role:</strong> {user.role}</p>

              {user.shippingAddress && (
                <p><strong>Shipping Address:</strong> {user.shippingAddress}</p>
              )}
              {user.paymentDetails && (
                <p><strong>Payment Details:</strong> {user.paymentDetails}</p>
              )}
              {user.permissions && (
                <p><strong>Permissions:</strong> {user.permissions}</p>
              )}

              <div className="mt-3">
                <button
                  className="btn btn-warning me-2"
                  onClick={() => setEditMode(true)}
                >
                  Update Profile
                </button>
                <button
                  className="btn btn-outline-danger"
                  onClick={handleDelete}
                >
                  Delete Account
                </button>
              </div>
            </>
          ) : (
            <>
              <div className="mb-2">
                <label><strong>Name:</strong></label>
                <input
                  className="form-control"
                  type="text"
                  value={updatedUser.name}
                  onChange={(e) =>
                    setUpdatedUser({ ...updatedUser, name: e.target.value })
                  }
                />
              </div>

              <div className="mb-2">
                <label><strong>Email:</strong></label>
                <input
                  className="form-control"
                  type="email"
                  value={updatedUser.email}
                  onChange={(e) =>
                    setUpdatedUser({ ...updatedUser, email: e.target.value })
                  }
                />
              </div>

              {updatedUser.shippingAddress !== undefined && (
                <div className="mb-2">
                  <label><strong>Shipping Address:</strong></label>
                  <input
                    className="form-control"
                    type="text"
                    value={updatedUser.shippingAddress}
                    onChange={(e) =>
                      setUpdatedUser({
                        ...updatedUser,
                        shippingAddress: e.target.value,
                      })
                    }
                  />
                </div>
              )}

              {updatedUser.paymentDetails !== undefined && (
                <div className="mb-2">
                  <label><strong>Payment Details:</strong></label>
                  <input
                    className="form-control"
                    type="text"
                    value={updatedUser.paymentDetails}
                    onChange={(e) =>
                      setUpdatedUser({
                        ...updatedUser,
                        paymentDetails: e.target.value,
                      })
                    }
                  />
                </div>
              )}

              <div className="mb-2">
                <label><strong>Role:</strong></label>
                <input className="form-control" type="text" value={updatedUser.role} readOnly />
              </div>

              {updatedUser.permissions && (
                <div className="mb-2">
                  <label><strong>Permissions:</strong></label>
                  <input className="form-control" type="text" value={updatedUser.permissions} readOnly />
                </div>
              )}

              <div className="mt-3">
                <button className="btn btn-success me-2" onClick={handleUpdate}>
                  Save Changes
                </button>
                <button
                  className="btn btn-secondary"
                  onClick={() => {
                    setEditMode(false);
                    setUpdatedUser(user); // Reset
                  }}
                >
                  Cancel
                </button>
              </div>
            </>
          )}
        </div>
      )}

      {/* üõí Cart Section */}
      {showCart && (
        <div className="card p-3 shadow mb-4">
          <h3>Your Cart</h3>
          <p>üõçÔ∏è Cart functionality coming soon...</p>
        </div>
      )}

      {/* üóÇÔ∏è Categories */}
      <Categories />
    </div>
  );
}

export default Welcome;
