// AdminPanel.js
import React, { useState, useEffect } from "react";
import axios from "axios";

export default function AdminPanel() {
  const [admins, setAdmins] = useState([]);
  const [form, setForm] = useState({
    name: "",
    role: "",
    permissions: "",
    email: "",
    password: ""
  });
  const [editingId, setEditingId] = useState(null);

  const token = localStorage.getItem("token");
  const API_URL = "http://localhost:5144/Admin";

  const fetchAdmins = () => {
    axios.get(`${API_URL}/ListofAdmins`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setAdmins(res.data))
      .catch(err => console.error("Error fetching admins:", err));
  };

  useEffect(() => {
    fetchAdmins();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const createAdmin = () => {
    axios.post(`${API_URL}/CreateAdmin`, form, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(() => {
        setForm({ name: "", role: "", permissions: "", email: "", password: "" });
        fetchAdmins();
      })
      .catch(err => console.error("Error creating admin:", err));
  };

  const deleteAdmin = (id) => {
    axios.delete(`${API_URL}/Delete?id=${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(() => fetchAdmins())
      .catch(err => console.error("Error deleting admin:", err));
  };

  const startEdit = (admin) => {
    setForm({
      name: admin.name,
      role: admin.role,
      permissions: admin.permissions,
      email: admin.email,
      password: ""
    });
    setEditingId(admin.adminId);
  };

  const updateAdmin = () => {
    axios.put(`${API_URL}/Update?id=${editingId}`, form, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(() => {
        setForm({ name: "", role: "", permissions: "", email: "", password: "" });
        setEditingId(null);
        fetchAdmins();
      })
      .catch(err => console.error("Error updating admin:", err));
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h2 className="text-2xl font-bold mb-6 text-center">Admin Panel</h2>

      {/* Form */}
      <div className="bg-white p-6 rounded-lg shadow-md mb-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
          <input name="name" placeholder="Name" value={form.name} onChange={handleChange} className="p-2 border rounded" />
          <input name="role" placeholder="Role" value={form.role} onChange={handleChange} className="p-2 border rounded" />
          <input name="permissions" placeholder="Permissions" value={form.permissions} onChange={handleChange} className="p-2 border rounded" />
          <input name="email" placeholder="Email" value={form.email} onChange={handleChange} className="p-2 border rounded" />
          <input name="password" type="password" placeholder="Password" value={form.password} onChange={handleChange} className="p-2 border rounded" />
        </div>
        {editingId ? (
          <button onClick={updateAdmin} className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded">
            Update Admin
          </button>
        ) : (
          <button onClick={createAdmin} className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded">
            Create Admin
          </button>
        )}
      </div>

      {/* Table */}
      <div className="overflow-x-auto bg-white rounded-lg shadow-md">
        <table className="min-w-full border-collapse">
          <thead>
            <tr className="bg-gray-200 text-gray-700">
              <th className="p-3 text-left">ID</th>
              <th className="p-3 text-left">Name</th>
              <th className="p-3 text-left">Email</th>
              <th className="p-3 text-left">Role</th>
              <th className="p-3 text-left">Permissions</th>
              <th className="p-3 text-center">Actions</th>
            </tr>
          </thead>
          <tbody>
            {admins.map(admin => (
              <tr key={admin.adminId} className="border-b hover:bg-gray-50">
                <td className="p-3">{admin.adminId}</td>
                <td className="p-3">{admin.name}</td>
                <td className="p-3">{admin.email}</td>
                <td className="p-3">{admin.role}</td>
                <td className="p-3">{admin.permissions}</td>
                <td className="p-3 flex gap-2 justify-center">
                  <button onClick={() => startEdit(admin)} className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded">
                    Edit
                  </button>
                  <button onClick={() => deleteAdmin(admin.adminId)} className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded">
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            {admins.length === 0 && (
              <tr>
                <td colSpan="6" className="p-3 text-center text-gray-500">No admins found.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
