// AdminPanel.js
import React, { useState, useEffect } from "react";
import axios from "axios";

export default function AdminPanel() {
  const [admins, setAdmins] = useState([]);
  const [form, setForm] = useState({
    name: "",
    role: "",
    permissions: "",
    email: "",
    password: ""
  });
  const [editingId, setEditingId] = useState(null);

  const token = localStorage.getItem("token"); // Make sure login saves token here
  const API_URL = "http://localhost:5144/Admin/ListofAdmins";

  // Fetch all admins
  const fetchAdmins = () => {
    axios.get(`${API_URL}/ListofAdmins`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setAdmins(res.data))
    .catch(err => console.error("Error fetching admins:", err));
  };

  useEffect(() => {
    fetchAdmins();
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Create new admin
  const createAdmin = () => {
    axios.post(`${API_URL}/CreateAdmin`, form, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(() => {
      setForm({ name: "", role: "", permissions: "", email: "", password: "" });
      fetchAdmins();
    })
    .catch(err => console.error("Error creating admin:", err));
  };

  // Delete admin
  const deleteAdmin = (id) => {
    axios.delete(`${API_URL}/Delete?id=${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(() => fetchAdmins())
    .catch(err => console.error("Error deleting admin:", err));
  };

  // Start editing
  const startEdit = (admin) => {
    setForm({
      name: admin.name,
      role: admin.role,
      permissions: admin.permissions,
      email: admin.email,
      password: admin.password || ""
    });
    setEditingId(admin.adminId);
  };

  // Update admin
  const updateAdmin = () => {
    axios.put(`${API_URL}/Update?id=${editingId}`, form, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(() => {
      setForm({ name: "", role: "", permissions: "", email: "", password: "" });
      setEditingId(null);
      fetchAdmins();
    })
    .catch(err => console.error("Error updating admin:", err));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Admin Panel</h2>

      {/* Admin form */}
      <div style={{ marginBottom: "20px" }}>
        <input name="name" placeholder="Name" value={form.name} onChange={handleChange} />
        <input name="role" placeholder="Role" value={form.role} onChange={handleChange} />
        <input name="permissions" placeholder="Permissions" value={form.permissions} onChange={handleChange} />
        <input name="email" placeholder="Email" value={form.email} onChange={handleChange} />
        <input name="password" type="password" placeholder="Password" value={form.password} onChange={handleChange} />
        {editingId ? (
          <button onClick={updateAdmin}>Update Admin</button>
        ) : (
          <button onClick={createAdmin}>Create Admin</button>
        )}
      </div>

      {/* Admins table */}
      <table border="1" cellPadding="8" style={{ width: "100%" }}>
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Email</th><th>Role</th><th>Permissions</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {admins.map(admin => (
            <tr key={admin.adminId}>
              <td>{admin.adminId}</td>
              <td>{admin.name}</td>
              <td>{admin.email}</td>
              <td>{admin.role}</td>
              <td>{admin.permissions}</td>
              <td>
                <button onClick={() => startEdit(admin)}>Edit</button>
                <button onClick={() => deleteAdmin(admin.adminId)}>Delete</button>
              </td>
            </tr>
          ))}
          {admins.length === 0 && (
            <tr>
              <td colSpan="6">No admins found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
