import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [profileError, setProfileError] = useState(null);

  const token = localStorage.getItem('token');

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get('https://localhost:7226/User/Profile', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(response.data);
      setProfileError(null);
    } catch (error) {
      setProfileError('Unable to load profile');
      console.error('Profile fetch error:', error);
    }
  };

  useEffect(() => {
    if (showProfile && !user && token) {
      fetchUserProfile();
    }
  }, [showProfile]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  const handleProfileClick = () => {
    setShowProfile(true);
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Welcome{user ? `, ${user.name}` : ''}</h2>
        <div>
          <button className="btn btn-primary me-2" onClick={handleProfileClick}>
            My Profile
          </button>
          <button className="btn btn-danger" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      {showProfile && (
        <div className="card p-4 mb-4">
          {profileError ? (
            <p className="text-danger">{profileError}</p>
          ) : user ? (
            <>
              <h4>My Profile</h4>
              {!editMode ? (
                <div>
                  <p><strong>Name:</strong> {user.name}</p>
                  <p><strong>Email:</strong> {user.email}</p>
                  <p><strong>Phone:</strong> {user.phoneNumber}</p>
                  <p><strong>Role:</strong> {user.role}</p>
                </div>
              ) : (
                <div>
                  {/* You can add editable form here if needed */}
                  <p>Edit mode enabled</p>
                </div>
              )}
            </>
          ) : (
            <p>Loading profile...</p>
          )}
        </div>
      )}

      <Categories />
    </div>
  );
}

export default Welcome;
