// AdminPanel.js
import React, { useState, useEffect } from "react";
import axios from "axios";

export default function AdminPanel() {
  const [admins, setAdmins] = useState([]);
  const [form, setForm] = useState({
    name: "",
    role: "",
    permissions: "",
    email: "",
    password: ""
  });
  const [editingId, setEditingId] = useState(null);

  const token = localStorage.getItem("token");
  const API_URL = "http://localhost:5144/Admin";

  // Fetch admins
  const fetchAdmins = () => {
    axios
      .get(`${API_URL}/ListofAdmins`, {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then((res) => setAdmins(res.data))
      .catch((err) => console.error("Error fetching admins:", err));
  };

  useEffect(() => {
    fetchAdmins();
  }, []);

  // Handle input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Create admin
  const createAdmin = () => {
    axios
      .post(`${API_URL}/CreateAdmin`, form, {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(() => {
        resetForm();
        fetchAdmins();
      })
      .catch((err) => console.error("Error creating admin:", err));
  };

  // Delete admin
  const deleteAdmin = (id) => {
    if (!window.confirm("Are you sure you want to delete this admin?")) return;
    axios
      .delete(`${API_URL}/Delete?id=${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(() => fetchAdmins())
      .catch((err) => console.error("Error deleting admin:", err));
  };

  // Edit admin
  const startEdit = (admin) => {
    setForm({
      name: admin.name,
      role: admin.role,
      permissions: admin.permissions,
      email: admin.email,
      password: ""
    });
    setEditingId(admin.adminId);
  };

  // Update admin
  const updateAdmin = () => {
    axios
      .put(`${API_URL}/Update?id=${editingId}`, form, {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(() => {
        resetForm();
        fetchAdmins();
      })
      .catch((err) => console.error("Error updating admin:", err));
  };

  // Reset form
  const resetForm = () => {
    setForm({ name: "", role: "", permissions: "", email: "", password: "" });
    setEditingId(null);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Admin Panel</h1>

      <div className="grid md:grid-cols-2 gap-6">
        {/* Form */}
        <div className="bg-white p-6 rounded-xl shadow-lg">
          <h2 className="text-xl font-semibold mb-4">
            {editingId ? "Edit Admin" : "Create New Admin"}
          </h2>
          <div className="space-y-3">
            <input
              name="name"
              placeholder="Name"
              value={form.name}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg focus:ring focus:ring-blue-300"
            />
            <input
              name="role"
              placeholder="Role"
              value={form.role}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg focus:ring focus:ring-blue-300"
            />
            <input
              name="permissions"
              placeholder="Permissions"
              value={form.permissions}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg focus:ring focus:ring-blue-300"
            />
            <input
              name="email"
              placeholder="Email"
              value={form.email}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg focus:ring focus:ring-blue-300"
            />
            <input
              name="password"
              type="password"
              placeholder="Password"
              value={form.password}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg focus:ring focus:ring-blue-300"
            />
            <div className="flex gap-3">
              {editingId ? (
                <>
                  <button
                    onClick={updateAdmin}
                    className="bg-yellow-500 text-white px-4 py-2 rounded-lg hover:bg-yellow-600"
                  >
                    Update
                  </button>
                  <button
                    onClick={resetForm}
                    className="bg-gray-400 text-white px-4 py-2 rounded-lg hover:bg-gray-500"
                  >
                    Cancel
                  </button>
                </>
              ) : (
                <button
                  onClick={createAdmin}
                  className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
                >
                  Create
                </button>
              )}
            </div>
          </div>
        </div>

        {/* Admins List */}
        <div className="bg-white p-6 rounded-xl shadow-lg overflow-x-auto">
          <h2 className="text-xl font-semibold mb-4">All Admins</h2>
          <table className="min-w-full border border-gray-200">
            <thead>
              <tr className="bg-gray-100">
                <th className="py-2 px-4 border">ID</th>
                <th className="py-2 px-4 border">Name</th>
                <th className="py-2 px-4 border">Email</th>
                <th className="py-2 px-4 border">Role</th>
                <th className="py-2 px-4 border">Permissions</th>
                <th className="py-2 px-4 border">Actions</th>
              </tr>
            </thead>
            <tbody>
              {admins.length > 0 ? (
                admins.map((admin) => (
                  <tr
                    key={admin.adminId}
                    className={`hover:bg-gray-50 ${
                      editingId === admin.adminId ? "bg-yellow-50" : ""
                    }`}
                  >
                    <td className="py-2 px-4 border">{admin.adminId}</td>
                    <td className="py-2 px-4 border">{admin.name}</td>
                    <td className="py-2 px-4 border">{admin.email}</td>
                    <td className="py-2 px-4 border">{admin.role}</td>
                    <td className="py-2 px-4 border">{admin.permissions}</td>
                    <td className="py-2 px-4 border flex gap-2">
                      <button
                        onClick={() => startEdit(admin)}
                        className="bg-yellow-500 text-white px-3 py-1 rounded-lg hover:bg-yellow-600"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => deleteAdmin(admin.adminId)}
                        className="bg-red-500 text-white px-3 py-1 rounded-lg hover:bg-red-600"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="text-center py-4 text-gray-500">
                    No admins found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
