import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

import Login from './components/Login';
import Register from './components/Register';
import AuthService from './services/AuthService';
import Welcome from './components/Welcome';
import CategoryService from './services/CategoryService';
import HeaderNav from './components/HeaderNav'; // import the nav

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = AuthService.getToken();
    if (token) {
      const user = AuthService.getUserFromToken(token);
      setUser(user);
    }
  }, []);

  const login = async (email, password) => {
    try {
      const token = await AuthService.login(email, password);
      const user = AuthService.getUserFromToken(token);
      setUser(user);
    } catch (error) {
      console.error('Error logging in', error);
    }
  };

  const logout = () => {
    AuthService.logout();
    setUser(null);
  };

  return (
    <Router>
      <HeaderNav user={user} logout={logout} />

      <Routes>
        <Route path="/" element={<Navigate to="/login" />} />
        <Route path="/Welcome" element={user ? <Welcome /> : <Navigate to="/login" />} />
        <Route path="/login" element={!user ? <Login login={login} /> : <Navigate to="/Welcome" />} />
        <Route path="/register" element={<Register />} />
        <Route path="/services/CategoryService" element={user ? <CategoryService /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
