import React, { useState, useEffect } from 'react';
import Categories from '../components/cards/Categories';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Welcome({ logout }) {
  const [user, setUser] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [showProfile, setShowProfile] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [updatedUser, setUpdatedUser] = useState(null);

  const navigate = useNavigate();

  // Fetch user profile once on mount
  useEffect(() => {
    const fetchUser = async () => {
      const token = localStorage.getItem('token');
      const email = localStorage.getItem('email');

      if (!token || !email) {
        navigate('/login');
        return;
      }

      try {
        const tokenPayload = JSON.parse(atob(token.split('.')[1]));
        const role = tokenPayload.role || 'User';
        const endpoint = role === 'Admin'
          ? `http://localhost:5144/Admin/Profile?email=${email}`
          : `http://localhost:5144/User/Profile?email=${email}`;

        const response = await axios.get(endpoint, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUser(response.data);
        setUpdatedUser(response.data);

        // Load cart from localStorage once user data is available
        const savedCart = localStorage.getItem(`cartItems_${response.data.userId}`);
        if (savedCart) {
          setCartItems(JSON.parse(savedCart));
        }
      } catch (error) {
        console.error('Failed to fetch user profile:', error);
        logout();
      }
    };

    fetchUser();
  }, [navigate, logout]);

  // Whenever cartItems change, save to localStorage (only if user is loaded)
  useEffect(() => {
    if (user?.userId) {
      localStorage.setItem(`cartItems_${user.userId}`, JSON.stringify(cartItems));
    }
  }, [cartItems, user]);

  const handleAddToCart = (product) => {
    setCartItems((prev) => [...prev, product]);
  };

  const handleRemoveFromCart = (index) => {
    setCartItems((prev) => prev.filter((_, i) => i !== index));
  };

  // ... Other handlers and JSX remain the same ...

  return (
    <div className="container-fluid mt-3">
      {/* Your header buttons and UI here */}

      {/* Show Profile and Cart sections */}
      {showProfile && user && (
        // Profile UI here
        <div> {/* your profile code */} </div>
      )}

      {showCart && (
        <div>
          <h3>Your Cart</h3>
          {cartItems.length === 0 ? (
            <p>Your cart is empty!</p>
          ) : (
            cartItems.map((item, index) => (
              <div key={index} className="d-flex justify-content-between">
                <span>{item.productName}</span>
                <button onClick={() => handleRemoveFromCart(index)}>Remove</button>
              </div>
            ))
          )}
        </div>
      )}

      <Categories onAddToCart={handleAddToCart} />
    </div>
  );
}

export default Welcome;
