import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProductList.css';  // Import the CSS styles

const API_URL = 'http://localhost:5144/Product';

const getToken = () => localStorage.getItem('token');

const getConfig = () => ({
  headers: {
    Authorization: `Bearer ${getToken()}`,
    'Content-Type': 'application/json',
  },
});

function ProductList() {
  const [products, setProducts] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState('');
  const [formMode, setFormMode] = useState('add'); // 'add' or 'edit'
  const [formProduct, setFormProduct] = useState({
    productId: 0,
    productName: '',
    description: '',
    price: '',
    category: '',
    imageURL: '',
  });
  const [message, setMessage] = useState('');

  // Fetch all products or by category
  const fetchProducts = (category = '') => {
    const url = category ? `${API_URL}/category/${category}` : API_URL;
    axios
      .get(url, getConfig())
      .then((res) => setProducts(res.data))
      .catch((err) => console.error('Error fetching products:', err));
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Delete product
  const handleDelete = (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;

    axios
      .delete(`${API_URL}/${id}`, getConfig())
      .then(() => {
        setMessage('Product deleted.');
        fetchProducts(categoryFilter);
      })
      .catch((err) => console.error('Error deleting product:', err));
  };

  // Handle input form change
  const handleInputChange = (e) => {
    setFormProduct({ ...formProduct, [e.target.name]: e.target.value });
  };

  // Handle form submit (add or update)
  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validation
    if (!formProduct.productName || !formProduct.price || !formProduct.category) {
      setMessage('Please fill all required fields: Name, Price, Category.');
      return;
    }

    const productData = {
      ...formProduct,
      price: Number(formProduct.price),
    };

    if (formMode === 'add') {
      // Add product
      axios
        .post(API_URL, productData, getConfig())
        .then(() => {
          setMessage('Product added successfully!');
          resetForm();
          fetchProducts(categoryFilter);
        })
        .catch((err) => {
          console.error('Error adding product:', err);
          setMessage('Failed to add product.');
        });
    } else if (formMode === 'edit') {
      // Update product
      axios
        .put(`${API_URL}/${formProduct.productId}`, productData, getConfig())
        .then(() => {
          setMessage('Product updated successfully!');
          resetForm();
          fetchProducts(categoryFilter);
        })
        .catch((err) => {
          console.error('Error updating product:', err);
          setMessage('Failed to update product.');
        });
    }
  };

  // Start editing a product
  const startEdit = (product) => {
    setFormProduct({
      productId: product.productId,
      productName: product.productName,
      description: product.description,
      price: product.price,
      category: product.category,
      imageURL: product.imageURL,
    });
    setFormMode('edit');
    setMessage('');
    window.scrollTo({ top: 0, behavior: 'smooth' }); // scroll up to form
  };

  // Reset form to add mode
  const resetForm = () => {
    setFormProduct({
      productId: 0,
      productName: '',
      description: '',
      price: '',
      category: '',
      imageURL: '',
    });
    setFormMode('add');
    setMessage('');
  };

  // Handle category filter button click
  const handleCategoryFilter = (category) => {
    setCategoryFilter(category);
    fetchProducts(category);
  };

  return (
    <div className="product-list-container">
      <h2>Product List</h2>

      {/* Add / Edit Product Form */}
      <h3>{formMode === 'add' ? 'Add New Product' : 'Edit Product'}</h3>
      <form onSubmit={handleSubmit} className="product-form">
        <input
          type="text"
          name="productName"
          placeholder="Product Name *"
          value={formProduct.productName}
          onChange={handleInputChange}
          required
        />
        <textarea
          name="description"
          placeholder="Description"
          value={formProduct.description}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Price *"
          value={formProduct.price}
          onChange={handleInputChange}
          required
          min="0"
          step="0.01"
        />
        <input
          type="text"
          name="category"
          placeholder="Category *"
          value={formProduct.category}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="imageURL"
          placeholder="Image URL"
          value={formProduct.imageURL}
          onChange={handleInputChange}
        />
        <div className="form-buttons">
          <button type="submit">{formMode === 'add' ? 'Add Product' : 'Update Product'}</button>
          {formMode === 'edit' && (
            <button type="button" onClick={resetForm} className="cancel-btn">
              Cancel
            </button>
          )}
        </div>
      </form>
      {message && <p className="message">{message}</p>}

      {/* Category Filters */}
      <div className="category-filters">
        <button
          className={!categoryFilter ? 'active' : ''}
          onClick={() => handleCategoryFilter('')}
        >
          All
        </button>
        <button
          className={categoryFilter === 'Woman' ? 'active' : ''}
          onClick={() => handleCategoryFilter('Woman')}
        >
          Woman
        </button>
        <button
          className={categoryFilter === 'Men' ? 'active' : ''}
          onClick={() => handleCategoryFilter('Men')}
        >
          Men
        </button>
        <button
          className={categoryFilter === 'Kids' ? 'active' : ''}
          onClick={() => handleCategoryFilter('Kids')}
        >
          Kids
        </button>
        <button
          className={categoryFilter === 'Beauty' ? 'active' : ''}
          onClick={() => handleCategoryFilter('Beauty')}
        >
          Beauty
        </button>
      </div>

      {/* Products List */}
      <ul className="products-list">
        {products.length === 0 && <li>No products found.</li>}
        {products.map((product) => (
          <li key={product.productId} className="product-item">
            <div className="product-info">
              <strong className="product-name">{product.productName}</strong>{' '}
              <span className="product-price">(${product.price.toFixed(2)})</span> -{' '}
              <span className="product-category">{product.category}</span>
              <br />
              {product.description && <small className="product-description">{product.description}</small>}
            </div>
            {product.imageURL && (
              <img
                src={product.imageURL}
                alt={product.productName}
                className="product-image"
              />
            )}
            <div className="product-actions">
              <button onClick={() => startEdit(product)} className="edit-btn">
                Edit
              </button>
              <button onClick={() => handleDelete(product.productId)} className="delete-btn">
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;
