import React, { useState, useEffect } from "react";
import axios from "axios";

const API_BASE = "https://localhost:5001"; // Replace with your actual backend URL

const getToken = () => localStorage.getItem("token");

const getConfig = () => ({
  headers: { Authorization: `Bearer ${getToken()}` },
});

function AdminPanel() {
  const [admins, setAdmins] = useState([]);
  const [formAdmin, setFormAdmin] = useState({
    AdminId: 0,
    Name: "",
    Role: "",
    Permissions: "",
    Email: "",
    Password: "",
  });
  const [editAdminId, setEditAdminId] = useState(null);
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetchAdmins();
  }, []);

  const fetchAdmins = async () => {
    try {
      const res = await axios.get(`${API_BASE}/Admin/ListofAdmins`, getConfig());
      setAdmins(res.data);
    } catch (err) {
      console.error("Error fetching admins:", err);
      setMessage("Failed to load admins.");
    }
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormAdmin((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmitForm = async (e) => {
    e.preventDefault();
    if (!formAdmin.Name || !formAdmin.Role || !formAdmin.Email) {
      setMessage("Please fill all required fields.");
      return;
    }

    try {
      if (editAdminId) {
        await axios.put(`${API_BASE}/Admin/Update?id=${formAdmin.AdminId}`, formAdmin, getConfig());
        setMessage("Admin updated successfully.");
      } else {
        await axios.post(`${API_BASE}/Admin/CreateAdmin`, formAdmin, getConfig());
        setMessage("Admin added successfully.");
      }
      resetForm();
      fetchAdmins();
    } catch (err) {
      console.error("Error submitting form:", err);
      setMessage("Operation failed.");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this admin?")) return;
    try {
      await axios.delete(`${API_BASE}/Admin/Delete?id=${id}`, getConfig());
      setMessage("Admin deleted.");
      fetchAdmins();
    } catch (err) {
      console.error("Error deleting admin:", err);
      setMessage("Delete failed.");
    }
  };

  const startEdit = (admin) => {
    setFormAdmin({
      AdminId: admin.AdminId,
      Name: admin.Name,
      Role: admin.Role,
      Permissions: admin.Permissions,
      Email: admin.Email,
      Password: "",
    });
    setEditAdminId(admin.AdminId);
    setMessage("");
  };

  const resetForm = () => {
    setFormAdmin({
      AdminId: 0,
      Name: "",
      Role: "",
      Permissions: "",
      Email: "",
      Password: "",
    });
    setEditAdminId(null);
    setMessage("");
  };

  const styles = {
    container: { maxWidth: 800, margin: "auto", padding: 20, fontFamily: "Arial" },
    input: { width: "100%", padding: 8, marginBottom: 10 },
    button: { padding: 10, marginRight: 10 },
    table: { width: "100%", borderCollapse: "collapse", marginTop: 20 },
    th: { backgroundColor: "#f0f0f0", padding: 8 },
    td: { padding: 8, border: "1px solid #ccc" },
  };

  return (
    <div style={styles.container}>
      <h2>Admin Management</h2>

      <form onSubmit={handleSubmitForm}>
        <input
          type="text"
          name="Name"
          placeholder="Name *"
          value={formAdmin.Name}
          onChange={handleFormChange}
          style={styles.input}
          required
        />
        <input
          type="email"
          name="Email"
          placeholder="Email *"
          value={formAdmin.Email}
          onChange={handleFormChange}
          style={styles.input}
          required
        />
        <input
          type="text"
          name="Role"
          placeholder="Role *"
          value={formAdmin.Role}
          onChange={handleFormChange}
          style={styles.input}
          required
        />
        <input
          type="text"
          name="Permissions"
          placeholder="Permissions"
          value={formAdmin.Permissions}
          onChange={handleFormChange}
          style={styles.input}
        />
        <input
          type="password"
          name="Password"
          placeholder={editAdminId ? "New Password (optional)" : "Password *"}
          value={formAdmin.Password}
          onChange={handleFormChange}
          style={styles.input}
          required={!editAdminId}
        />

        <button type="submit" style={styles.button}>
          {editAdminId ? "Update Admin" : "Add Admin"}
        </button>
        {editAdminId && (
          <button type="button" onClick={resetForm} style={styles.button}>
            Cancel
          </button>
        )}
      </form>

      {message && <p>{message}</p>}

      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.th}>Name</th>
            <th style={styles.th}>Email</th>
            <th style={styles.th}>Role</th>
            <th style={styles.th}>Permissions</th>
            <th style={styles.th}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {admins.length === 0 ? (
            <tr>
              <td colSpan="5" style={{ textAlign: "center" }}>No admins found.</td>
            </tr>
          ) : (
            admins.map((admin) => (
              <tr key={admin.AdminId}>
                <td style={styles.td}>{admin.Name}</td>
                <td style={styles.td}>{admin.Email}</td>
                <td style={styles.td}>{admin.Role}</td>
                <td style={styles.td}>{admin.Permissions}</td>
                <td style={styles.td}>
                  <button onClick={() => startEdit(admin)} style={styles.button}>Edit</button>
                  <button onClick={() => handleDelete(admin.AdminId)} style={{ ...styles.button, color: "red" }}>Delete</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPanel;
