import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories'; // Adjust path if needed
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({ email: '', password: '', contactNumber: '' });

  const token = localStorage.getItem('token');

  const fetchUserProfile = async () => {
    if (!token) return;
    try {
      const { data } = await axios.get('http://localhost:5144/User/Profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(data);
      setFormData({
        email: data.email,
        password: '',
        contactNumber: data.contactNumber || data.phone || ''
      });
    } catch (e) {
      console.error('Profile fetch failed', e);
    }
  };

  const handleProfileClick = () => {
    setShowProfile(!showProfile);
    if (!showProfile) fetchUserProfile();
    setEditMode(false);
  };

  const handleInputChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleUpdate = async () => {
    try {
      await axios.patch('http://localhost:5144/User/Update', null, {
        params: {
          userId: user.userId,
          email: formData.email,
          password: formData.password,
          ContactNumber: formData.contactNumber
        },
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Profile updated');
      setEditMode(false);
      fetchUserProfile();
    } catch (e) {
      alert('Update failed');
      console.error(e);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure to delete your profile?')) return;
    try {
      await axios.delete('http://localhost:5144/User/Delete', {
        headers: { Authorization: `Bearer ${token}` },
        data: user
      });
      alert('Deleted');
      localStorage.removeItem('token');
      window.location.href = '/login';
    } catch (e) {
      alert('Delete failed');
      console.error(e);
    }
  };

  if (!token)
    return <div className="text-center mt-5">Please login to view your profile.</div>;

  return (
    <div className="container-fluid p-0" style={{ background: '#f0f8ff', minHeight: '100vh' }}>
      {/* Navbar */}
      <nav className="navbar navbar-light bg-white shadow-sm px-4 mb-3">
        <div className="container-fluid justify-content-end">
          <div className="text-end">
            <div className="fw-bold mb-2 text-primary fs-5">
              {user?.name ? `Welcome, ${user.name}` : 'Welcome!'}
            </div>
            <div className="d-flex gap-2">
              <button
                className="btn btn-primary"
                onClick={() => window.location.href = "/welcome"}
              >
                Home
              </button>
              <button className="btn btn-secondary" onClick={handleProfileClick}>
                My Profile
              </button>
              <button
                className="btn btn-danger"
                onClick={() => {
                  localStorage.removeItem('token');
                  window.location.href = '/login';
                }}
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* Page Content */}
      <div className="container">
        <h2 className="text-center mb-1">Welcome to Shop for Life</h2>
        <p className="text-center text-muted mb-4" style={{ fontStyle: 'italic' }}>
          "Shopping is the best entertainment to entertain Mer"
        </p>

        {showProfile && user ? (
          <div className="card shadow-sm mb-4">
            <div className="card-body">
              <div className="d-flex align-items-center mb-3">
                <img
                  src={`https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=random&rounded=true&size=80`}
                  alt="Avatar"
                  className="me-3 rounded-circle"
                />
                <h4 className="mb-0">{user.name}</h4>
              </div>

              <p><strong>Email:</strong> {user.email}</p>

              {user.role?.toLowerCase() === 'admin' ? (
                <>
                  <p><strong>Role:</strong> {user.role}</p>
                  <p><strong>Permissions:</strong> {user.permissions || 'N/A'}</p>
                </>
              ) : (
                <>
                  <p><strong>Contact:</strong> {user.contactNumber || user.phone || 'N/A'}</p>
                  <p><strong>Shipping Address:</strong> {user.shippingAddress || 'N/A'}</p>
                  <p><strong>Payment Details:</strong> {user.paymentDetails || 'N/A'}</p>
                  <p><strong>Role:</strong> {user.role}</p>
                </>
              )}

              {!editMode ? (
                <>
                  <button className="btn btn-warning me-2" onClick={() => setEditMode(true)}>Edit Profile</button>
                  <button className="btn btn-outline-danger" onClick={handleDelete}>Delete Account</button>
                </>
              ) : (
                <>
                  <div className="mb-3">
                    <label>Email:</label>
                    <input
                      type="email"
                      className="form-control"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="mb-3">
                    <label>Password:</label>
                    <input
                      type="password"
                      className="form-control"
                      name="password"
                      value={formData.password}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="mb-3">
                    <label>Contact Number:</label>
                    <input
                      type="number"
                      className="form-control"
                      name="contactNumber"
                      value={formData.contactNumber}
                      onChange={handleInputChange}
                    />
                  </div>
                  <button className="btn btn-success me-2" onClick={handleUpdate}>Save</button>
                  <button className="btn btn-secondary" onClick={() => setEditMode(false)}>Cancel</button>
                </>
              )}
            </div>
          </div>
        ) : (
          <>
            <h3 className="text-center mt-5 mb-4">Explore More Categories</h3>
            <div className="container-fluid d-flex justify-content-center gap-4 flex-wrap">
              <Categories />
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Welcome;
