import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5144/Product';

const getToken = () => localStorage.getItem('token');

const getConfig = () => ({
  headers: {
    Authorization: `Bearer ${getToken()}`,
    'Content-Type': 'application/json',
  },
});

function ProductList() {
  const [products, setProducts] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState('');
  const [formMode, setFormMode] = useState('add'); // 'add' or 'edit'
  const [formProduct, setFormProduct] = useState({
    productId: 0,
    productName: '',
    description: '',
    price: '',
    category: '',
    imageURL: '',
  });
  const [message, setMessage] = useState('');

  // Fetch all products or by category
  const fetchProducts = (category = '') => {
    const url = category ? `${API_URL}/category/${category}` : API_URL;
    axios
      .get(url, getConfig())
      .then((res) => setProducts(res.data))
      .catch((err) => console.error('Error fetching products:', err));
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Delete product
  const handleDelete = (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;

    axios
      .delete(`${API_URL}/${id}`, getConfig())
      .then(() => {
        setMessage('Product deleted.');
        fetchProducts(categoryFilter);
      })
      .catch((err) => console.error('Error deleting product:', err));
  };

  // Handle input form change
  const handleInputChange = (e) => {
    setFormProduct({ ...formProduct, [e.target.name]: e.target.value });
  };

  // Handle form submit (add or update)
  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validation
    if (!formProduct.productName || !formProduct.price || !formProduct.category) {
      setMessage('Please fill all required fields: Name, Price, Category.');
      return;
    }

    const productData = {
      ...formProduct,
      price: Number(formProduct.price),
    };

    if (formMode === 'add') {
      // Add product
      axios
        .post(API_URL, productData, getConfig())
        .then(() => {
          setMessage('Product added successfully!');
          resetForm();
          fetchProducts(categoryFilter);
        })
        .catch((err) => {
          console.error('Error adding product:', err);
          setMessage('Failed to add product.');
        });
    } else if (formMode === 'edit') {
      // Update product
      axios
        .put(`${API_URL}/${formProduct.productId}`, productData, getConfig())
        .then(() => {
          setMessage('Product updated successfully!');
          resetForm();
          fetchProducts(categoryFilter);
        })
        .catch((err) => {
          console.error('Error updating product:', err);
          setMessage('Failed to update product.');
        });
    }
  };

  // Start editing a product
  const startEdit = (product) => {
    setFormProduct({
      productId: product.productId,
      productName: product.productName,
      description: product.description,
      price: product.price,
      category: product.category,
      imageURL: product.imageURL,
    });
    setFormMode('edit');
    setMessage('');
    window.scrollTo({ top: 0, behavior: 'smooth' }); // scroll up to form
  };

  // Reset form to add mode
  const resetForm = () => {
    setFormProduct({
      productId: 0,
      productName: '',
      description: '',
      price: '',
      category: '',
      imageURL: '',
    });
    setFormMode('add');
    setMessage('');
  };

  // Handle category filter button click
  const handleCategoryFilter = (category) => {
    setCategoryFilter(category);
    fetchProducts(category);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Product List</h2>

      {/* Add / Edit Product Form */}
      <h3>{formMode === 'add' ? 'Add New Product' : 'Edit Product'}</h3>
      <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          name="productName"
          placeholder="Product Name *"
          value={formProduct.productName}
          onChange={handleInputChange}
          required
        />
        <br />
        <textarea
          name="description"
          placeholder="Description"
          value={formProduct.description}
          onChange={handleInputChange}
        />
        <br />
        <input
          type="number"
          name="price"
          placeholder="Price *"
          value={formProduct.price}
          onChange={handleInputChange}
          required
          min="0"
          step="0.01"
        />
        <br />
        <input
          type="text"
          name="category"
          placeholder="Category *"
          value={formProduct.category}
          onChange={handleInputChange}
          required
        />
        <br />
        <input
          type="text"
          name="imageURL"
          placeholder="Image URL"
          value={formProduct.imageURL}
          onChange={handleInputChange}
        />
        <br />
        <button type="submit" style={{ marginTop: '10px' }}>
          {formMode === 'add' ? 'Add Product' : 'Update Product'}
        </button>
        {formMode === 'edit' && (
          <button
            type="button"
            onClick={resetForm}
            style={{ marginLeft: '10px', backgroundColor: 'gray', color: 'white' }}
          >
            Cancel
          </button>
        )}
      </form>
      {message && <p>{message}</p>}

      {/* Category Filters */}
      <div style={{ marginBottom: '20px' }}>
        <button onClick={() => handleCategoryFilter('')}>All</button>
        <button onClick={() => handleCategoryFilter('Woman')} style={{ marginLeft: '10px' }}>
          Woman
        </button>
        <button onClick={() => handleCategoryFilter('Men')} style={{ marginLeft: '10px' }}>
          Men
        </button>
        <button onClick={() => handleCategoryFilter('Kids')} style={{ marginLeft: '10px' }}>
          Kids
        </button>
        <button onClick={() => handleCategoryFilter('Beauty')} style={{ marginLeft: '10px' }}>
          Beauty
        </button>
      </div>

      {/* Products List */}
      <ul>
        {products.length === 0 && <li>No products found.</li>}
        {products.map((product) => (
          <li key={product.productId} style={{ marginBottom: '15px' }}>
            <strong>{product.productName}</strong> (${product.price}) - {product.category}
            <br />
            {product.description && <small>{product.description}</small>}
            <br />
            {product.imageURL && (
              <img
                src={product.imageURL}
                alt={product.productName}
                style={{ width: '100px', height: 'auto', marginTop: '5px' }}
              />
            )}
            <br />
            <button
              onClick={() => startEdit(product)}
              style={{ marginRight: '10px', color: 'blue' }}
            >
              Edit
            </button>
            <button
              onClick={() => handleDelete(product.productId)}
              style={{ color: 'red' }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;
