import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);

  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get('https://localhost:7266/User/profile', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(response.data);
    } catch (error) {
      console.error('Unable to load profile', error);
    }
  };

  const handleProfileClick = () => {
    setShowProfile(true);
    fetchUserProfile();
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    window.location.href = '/login';
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          {token && user && <h3>Welcome, {user.name}</h3>}
        </div>
        <div>
          {!token ? (
            <a className="btn btn-primary me-2" href="/login">
              Login
            </a>
          ) : (
            <>
              <button className="btn btn-info me-2" onClick={handleProfileClick}>
                My Profile
              </button>
              <button className="btn btn-danger" onClick={handleLogout}>
                Logout
              </button>
            </>
          )}
        </div>
      </div>

      {showProfile && user ? (
        <div className="card">
          <div className="card-header">My Profile</div>
          <div className="card-body">
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Role:</strong> {user.role}</p>
          </div>
        </div>
      ) : (
        <Categories />
      )}
    </div>
  );
}

export default Welcome;
